void init()
{
    #ISOCountryRegionCodes
    VendParameters  vp;
    boolean         enable;
    boolean         historyButtonsVisible;
    // <GIN>
    TaxWithholdParameters_IN    taxWithholdParameters = TaxWithholdParameters_IN::find();
    // </GIN>

    // <GIN>
    countryRegionIN         = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);
    // </GIN>
    // <GAU>
    countryRegionAU         = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoAU]);
    // </GAU>
    // <GJP>
    countryRegionJP         = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoJP]);
    // </GJP>

    traceFilter = element.args().parmObject() as InventTrackingVendorSummaryFilter;
    if (traceFilter)
    {
        traceFilter.parmFormRun(this);
    }

    typeComboBox = DirPartyFormHandler::updateTypeComboBox(element, formControlStr(VendTable, Type));

    // <GSA>
    zakatParameterEnabled = LedgerParameters::find().IsZakatEnabled_SA;
    // </GSA>

    super();

    if (! VendTableVendorApprovalOrgNameOverriddenFlight::instance().isEnabled())
    {
        element.markChangeProposalFields();
    }

    callerRecord = element.args().record();

    TaxVATNumTable::enableLookupVATNum(salesTax_VatNum);

    if (CompanyNAFCode::nafCodeTableEmpty())
    {
        vendTable_ds.object(fieldNum(VendTable, CompanyIdSiret)).visible(false);
        vendTable_ds.object(fieldNum(VendTable, CompanyNAFCode)).visible(false);
    }

    vp = VendParameters::find();

    if (PurchParameters::find().PurchaseCycle)
    {
        purchaseCycle.visible(true);
    }

    if (vp.VendInvoiceRefNumValidation_FI)
    {
        vendTable_ds.object(fieldNum(VendTable, PaymId)).allowEdit(false);
    }

    enable = (vp.editUseInvoiceMatching() == NoYes::Yes);
    vendTable_ds.object(fieldNum(VendTable, VendPriceToleranceGroupId)).allowEdit(enable);
    vendTable_ds.object(fieldNum(VendTable, VendPriceToleranceGroupId)).skip(!enable);

    // Hide matching policy button
    matchingPolicy.visible(enable);
    priceTolerance.visible(enable);

    // hide totals tolerance button if turned off in AP setup
    if (!vp.UseTotalPriceMatching)
    {
        vendTotalPriceTolerance.visible(false);
    }

    if (!InventParameters::find().UseQualityManagement)
    {
        element.hideQualityManagement();
    }

    historyButtonsVisible = VendParameters::find(false).editUseInvoiceMatching(false) == NoYes::Yes;

    invoiceHistory.visible(historyButtonsVisible);
    approvalJournalHistory.visible(historyButtonsVisible);
    paymentHistory.visible(isConfigurationkeyEnabled(configurationKeyNum(LogisticsBasic)));

    // Hide totals tolerance button if turned off in AP setup
    if (!VendParameters::find().UseTotalPriceMatching)
    {
        vendTotalPriceTolerance.visible(false);
    }

    mainDatasources = [vendTable_ds.id(), dirPartyTable_ds.id(), dirPartyTable_DirOrganization_ds.id(), dirPartyTable_DirPerson_ds.id(), dirPersonName_ds.id(), dirPartyTable_DirOrganizationBase_ds.id()];

    addressBooksCtrl = SysLookupMultiSelectCtrl::construct(element, addressBooks, queryStr(DirAddressBookLookup));

    partyForm = new DirPartyFormHandler_Entity(element);
    partyForm.setDatasourceCaller(vendTable_ds);
    partyForm.setDatasourceParty(dirPartyTable_ds);
    partyForm.setDatasourcePartyName(dirPersonName_ds);
    partyForm.ctrlTabPageGeneral(tabGeneral);
    partyForm.parmAddressBookCtrl(addressBooksCtrl);
    partyForm.setNameControls(org_Name, name_FirstName, name_MiddleName, name_LastName);

    OffsetAccount_OffsetAccount.parmIsDefaultAccount(true);

    // <GSA>
    tabZakat_SA.visible(zakatParameterEnabled);
    vendContractZakat_SA.visible(zakatParameterEnabled);
    // </GSA>

    setPartyType = false;

    // <GIN>
    if (countryRegionIN)
    {
        element.setVisible_IN();
        withholdingTax.visible(false);
        if (TaxParameters::isCustomsEnable_IN())
        {
            element.setCustomsGroupVisible_IN();
        }

        taxInformationVendTable_IN_ds.object(fieldNum(TaxInformationVendTable_IN, tdsGroup)).visible(taxWithholdParameters.tdsActivate);
        taxInformationVendTable_IN_ds.object(fieldNum(TaxInformationVendTable_IN, tcsGroup)).visible(taxWithholdParameters.tcsActivate);
    }
    // </GIN>

    // <GAU>
    if (countryRegionAU)
    {
        VendTable_DS.object(fieldNum(VendTable, EthnicOriginId)).visible(false);
        VendTable_DS.object(fieldNum(VendTable, VeteranOwned)).visible(false);
        VendTable_DS.object(fieldNum(VendTable, DisabledOwned)).visible(false);
    }
    // </GAU>

    // <GJP>
    if (!VendConsInvoiceType_JP::isVendConsInvoiceEnabled())
    {
        vendTable_ds.object(fieldNum(VendTable, ConsDay_JP)).visible(false);
        consDay_JP.visible(false);
    }
    // </GJP>

    //disables prepay group when posting definition is enabled
    advance.enabled (!LedgerParameters::find().JournalizingDefinitionEnableValue);

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        retailVendTable_ds.allowCreate(true);
    }
    // <GEEU>
    if (VendAdvanceInvoice::isDeactivated())
    {
        advance.visible(false);
    }
    // </GEEU>

    if (!VendRecurringInvoiceFeature::isEnabled())
    {
        VendRecurrenceInvoice.visible(false);
    }

    if (traceFilter)
    {
        traceFilter.formInitPost();
        this.disableEnableControlsForItemTracing();
    }

    if (Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::Purchase)
        || Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::Journal))
    {
        VendTable_ds.object(fieldNum(VendTable, OverrideSalesTax)).visible(true);
    }

    boolean shouldAccrueSalesTaxTypeVisible = (TaxIntegrationUtils::isTaxIntegrationEnabledForTaxSolutionProvider() && TaxIntegrationTaxServiceParameters::find().AccrueUseTax)
                                                || FeatureStateProvider::isFeatureEnabled(TaxUseTaxAssessmentAdjustSalesTaxFeature::instance());
    VendTable_ds.object(fieldNum(VendTable, AccrueSalesTaxType)).visible(shouldAccrueSalesTaxTypeVisible);

    if (PurchIsSupplierShipmentDatesControlsVisibleFlight::instance().isEnabled())
    {
        Delivery_PurchaseShipCalendarId.visible(PurchParameters::find().IsSupplierShipmentDatesEnabled);
    }
}

