public void refreshActionPane()
{
    vendTable_ds.executeQuery();
}

public void run()
{
    int tabNumber = 2;

    if (element.args().parm() == #Address)
    {
        tabNumber = 5;
    }
    super();

    //
    // If this form is called from Home/Global Address Book then we need to retrieve
    // the full buffer of DirPartyTable so that all the dependent tables will have
    // data along with parent table otherwise the fields related to DirPerson will be
    // disabled and will show text as Unretrieved
    //
    if (element.args().caller())
    {
        if (element.args().caller().name() == formStr(DirPartyTable))
        {
            dirPartyTable.data(DirPartyTable::findRec(callerRecord.RecId));
            dirPartyTable_ds.rereadReferenceDataSources();

            if (dirPartyTable.type() == DirPartyType::Person)
            {
                //
                // Fetch the existing name record and populate the table buffer with it so that
                // it does not attempt to create a new date effective version record for the table
                //
                dirPersonName.data(DirPersonName::find(DirPartyTable.RecId));
            }
        }
    }

    setPartyType = true;

    if (RetailSMB::IsRetailSMBEnabled())
    {
        // Hide info parts
        RetailUtils::HidePart(element, identifierStr(PartyRoles));
    }
}

void salesPriceRoundingModified()
{
    if (retailVendTable.SalesPriceRounding == RetailSalesPriceRoundingBase::None)
    {
        retailVendTable_ds.object(fieldNum(RetailVendTable, PricePointRoundingType)).enabled(false);
        retailVendTable_ds.object(fieldNum(RetailVendTable, PricePointGroupId)).enabled(false);
        retailVendTable_ds.object(fieldNum(RetailVendTable, RoundingMethod)).enabled(false);
    }
    else if (retailVendTable.SalesPriceRounding == RetailSalesPriceRoundingBase::Rounding)
    {
        retailVendTable_ds.object(fieldNum(RetailVendTable, PricePointRoundingType)).enabled(false);
        retailVendTable_ds.object(fieldNum(RetailVendTable, PricePointGroupId)).enabled(false);
        retailVendTable_ds.object(fieldNum(RetailVendTable, RoundingMethod)).enabled(true);
    }
    else if (retailVendTable.SalesPriceRounding == RetailSalesPriceRoundingBase::PricePoints)
    {
        retailVendTable_ds.object(fieldNum(RetailVendTable, PricePointRoundingType)).enabled(true);
        retailVendTable_ds.object(fieldNum(RetailVendTable, PricePointGroupId)).enabled(true);
        retailVendTable_ds.object(fieldNum(RetailVendTable, RoundingMethod)).enabled(false);
    }
}

public boolean selectControl(FormControl _control)
{
    boolean ret;
    if (dirPartyTable.InstanceRelationType == tableNum(DirPerson))
    {
        ret = partyForm.selectControl(_control, dirPersonName);

        if (!ret)
        {
            return false;
        }
    }

    ret = super(_control);

    return ret;
}

public void setCustomsGroupVisible_IN()
{
    taxinformationVendTable_IN_ds.object(fieldNum(TaxInformationVendTable_IN, IsPreferential)).allowEdit(vendTable.isForeign());
}

void setTaxWithholdEnabled_IN()
{
    taxInformationVendTable_IN_ds.object(fieldNum(TaxInformationVendTable_IN, tdsGroup)).enabled(
                                (!taxInformationVendTable_IN.tcsGroup && !vendTable.TaxWithholdGroup));
    taxInformationVendTable_IN_ds.object(fieldNum(TaxInformationVendTable_IN, tcsGroup)).enabled(
                                (!taxInformationVendTable_IN.tdsGroup && !vendTable.TaxWithholdGroup));
    vendTable_ds.object(fieldNum(VendTable, TaxWithholdGroup)).enabled(
                                (!taxInformationVendTable_IN.tdsGroup && !taxInformationVendTable_IN.tcsGroup));
}

public void setVisible_IN()
{
    TaxParameters       taxParametersLoc;

    taxParametersLoc = TaxParameters::find();

    tabTaxInformation_IN.visible(TaxParameters::checkTaxParameters_IN() || TaxWithholdParameters_IN::checkTaxParameters());

    vendTable_ForeignVendor_IN.visible(taxParametersLoc.Customs_IN);
    vendTable_PreferentialVendor_IN.visible(taxParametersLoc.Customs_IN);
    vendTable_GTAVendor_IN.visible(taxParametersLoc.ServiceTax_IN || TaxSolutionScopeIntegrationUtil::isCompanyEnabled());
    vendTable_SSIVendor_IN.visible(taxParametersLoc.Excise_IN);
    vendTable_SSIValidityDate_IN.visible(taxParametersLoc.Excise_IN);
    vat_in.visible(taxParametersLoc.vat_in);
}

/// <summary>
/// Enables or disables the blocked release date field.
/// </summary>
void toggleBlockedReleaseDate()
{
    if (vendTable.Blocked == CustVendorBlocked::No || vendTable.Blocked == CustVendorBlocked::Never)
    {
        administration_releaseDate.enabled(false);
    }
    else
    {
        administration_releaseDate.enabled(true);
    }
}

public void updateControls()
{
    boolean         newRecord = (vendTable.RecId == 0);
    boolean         newPartyRecord = (dirPartyTable.RecId == 0);

    if (!newPartyRecord)
    {
        addressBooksCtrl.set(dirPartyTable.addressBooks());
    }

    typeComboBox.select(enum2int(dirPartyTable.baseType()));

    type.allowEdit(newRecord);
    lastPartyType = any2Enum(typeComboBox.selection());

    AddToOutlookContacts.visible(!smmOutlookSyncDeprecationHandler::isOutlookSyncDisabled());
}

public void updateDesign()
{
    DirPartyType partyType = dirPartyTable.type();
    boolean      isInternalOrg = (partyType != DirPartyType::Organization && partyType != DirPartyType::Person);
    boolean      isOrganization = (partyType == DirPartyType::Organization);
    boolean      isPerson = (partyType == DirPartyType::Person);

    if (isPerson)
    {
        VendTable_Name.label("@AccountsReceivable:PersonName");
        Org_Name.label("@AccountsReceivable:PersonName");
    }

    if (isOrganization || isInternalOrg)
    {
        VendTable_Name.label("@AccountsReceivable:OrganizationName");
        Org_Name.label("@AccountsReceivable:OrganizationName");
    }

    if (tabGeneral.isActivePage())
    {
        // Organization
        OrgGroup.visible(!isPerson);
        OrgDetailGroup.visible(isOrganization);

        Org_KnownAs.visible(isOrganization);
        OrgPhoneticGroup_PhoneticName.visible(isOrganization);
        Org_LanguageId.visible(isOrganization);

        // Person
        PersonGroup1.visible(isPerson);
        PersonGroup2.visible(isPerson);
        PersonGroup3.visible(isPerson);
        Birthday.visible(isPerson);
        Anniversary.visible(isPerson);

        Person_LanguageId.visible(isPerson);
        PersonDetailGroup_ChildrenNames.visible(isPerson);
        PersonDetailGroup_Hobbies.visible(isPerson);
    }

    if (!isInternalOrg && (setPartyType || tabGeneral.isActivePage()))
    {
        if (setPartyType)
        {
            partyForm.parmPartyType(partyType);
            setPartyType = false;
        }
        partyForm.setDatasourcePartySubType(isPerson ? dirPartyTable_DirPerson_ds : dirPartyTable_DirOrganizationBase_ds);
        partyForm.manageFields();
    }
}

