/// <summary>
/// Show the dialog listing changes to change-controlled fields.
/// </summary>
protected void showChangeProposalDialog()
{
    Args args = new Args();
    args.menuItemName(menuItemDisplayStr(VendChangeProposal));
    args.menuItemType(MenuItemType::Display);
    args.record(VendTable);
    args.parm("@AccountsPayable:NotificationChangesRequireApproval");

    new MenuFunction(menuItemDisplayStr(VendChangeProposal), MenuItemType::Display).run(args);
}

/// <summary>
/// Determines if changes to a vendor can be submitted to workflow.
/// </summary>
/// <returns>True if changes can be submitted; otherwise false</returns>
public boolean canSubmitToWorkflow()
{
    boolean ret = true;

    if (!VendTable.RecId)
    {
        ret = false;
    }

    if (VendTable.WorkflowState == VendTableChangeProposalWorkflowState::NotSubmitted)
    {
        VendTableChangeProposal changeProposal;

        changeProposal.disableCache(true); // Should not be able to submit based on stale data.
        select RecId from changeProposal
            where changeProposal.VendTable == VendTable.RecId;

        if (!changeProposal)
        {
            ret = false;
        }
    }
    else
    {
        ret = false;
    }

    return ret;
}

/// <summary>
/// Handles a change in a change-controlled field.
/// </summary>
/// <param name = "_fieldNumVend">The number of the changed field in <c>VendTable</c>.</param>
/// <param name = "_fieldNumProp">The number of the changed field in <c>VendTableChangeProposal</c>.</param>
/// <param name = "_custTable">The pointer to a buffer to manipulate as a result of the change.</param>
/// <param name = "_controlledField">The enumeration representing the changed field. Used to check if change control is enabled on the field.</param>
/// <returns>True if changes have been reverted due to change control, otherwise false.</returns>
[Hookable(false)]
public boolean modifiedControlledField(FieldId _fieldNumVend, FieldId _fieldNumProp, VendTable _vendTable, VendTableChangeProposalField _controlledField)
{
    VendTable originalTable = _vendTable.orig();
    boolean ret;

    if (originalTable.(_fieldNumVend) != _vendTable.(_fieldNumVend)
            && VendTableChangeProposalFieldEnablement::isEnabledFor(_controlledField))
    {
        if (!_vendTable)
        {
            element.showWarningNoChangeControlOnCreation();
        }
        else
        {
            if (_vendTable.canModifyChangeProposal())
            {
                ttsbegin;
                // create or update an approval entry
                VendTableChangeProposal change = VendTableChangeProposal::findOrCreate(_vendTable.RecId, true);
                change.(_fieldNumProp) = _vendTable.(_fieldNumVend);
                change.setFlagForChangedField(_controlledField);
                change.update();
                ttscommit;
            }

            // reset the field to original (must be after change proposal creation)
            _vendTable.(_fieldNumVend) = originalTable.(_fieldNumVend);

            if (VendTable_ds.validateWrite())
            {
                VendTable_ds.write();
                // pop dialog
                element.showChangeProposalDialog();
            }

            ret = true;
        }
    }

    return ret;
}

private void showWarningNoChangeControlOnCreation()
{
    if (!showedWarningNoChangeControlOnCreation)
    {
        showedWarningNoChangeControlOnCreation = true; // Avoid repeated spam
        info("@AccountsPayable:VendorApprovalNotActive");
    }
}

public int task(int _taskID)
{
    int ret;

    if (_taskID == #taskSwitchToDetailsView)
    {
        // If the vendor account number generation should be delayed, display info message and disable account number control.
        // Cover scenario that a vendor is created from party form.
        // In this scenario we need to check whether the vendor.accountNum is null, 
        //      it is used to prevent this message show up when a vendor is copied from tax exempt number search;
        //      it is also used to prevent this message show up twice when creating a new customer from NEW button on vendor form.
        VendTable vendor = VendTable_ds.cursor();

        if (vendor.AccountNum == null && element.isDelayNumberSequenceGeneration())
        {
            info("@AccountsPayable:VendAccountNumberGenerationDelay");
            VendTable_ds.object(fieldnum(VendTable, AccountNum)).allowedit(false);
        }  
    }
    else if (_taskID == #taskRestore)
    {
        if (!vendTable_ds.cursor().RecId) // Cursor not saved.
        {
            numberSeqFormHandler.formMethodClose();
        }
    }

    ret = super(_taskID);

    return ret;
}

public boolean isNumberSequenceManual()
{
    NumberSequenceTable numSequence = NumberSequenceTable::find(element.numberSeqFormHandler().parmNumberSequenceId());

    return numSequence.Manual;
}

public boolean isDelayNumberSequenceGeneration()
{
    if (CustVendCopyDataUtil::isVendAccountNumSequenceGlobal(curExt()) || VendGroup::doesAnyGroupHaveVendAccountNumberSequenceSet())
    {
        return true;
    }
    else 
    {
        return false;
    }
}

public void setIsCopied(boolean _isCopied)
{
    isCopied = _isCopied;
}

private void updateVendorChargedSalesTaxToleranceController()
{
    boolean shouldToleranceVisible = VendTable.AccrueSalesTaxType == TaxAccrueSalesTaxType::Advanced;

    vendTable_ds.object(fieldNum(VendTable, TaxVendorChargeTaxToleranceAmount)).visible(shouldToleranceVisible);
    vendTable_ds.object(fieldNum(VendTable, TaxVendorChargeTaxTolerancePercent)).visible(shouldToleranceVisible);
    vendTable_ds.object(fieldNum(VendTable, TaxVendorChargeTaxToleranceValidation)).visible(shouldToleranceVisible);
}

