        vendTable.nit_br = '';
    }

    this.object(fieldNum(VendTable, nit_br)).enabled(_enabled);
}

public void partyTypeModified_BR()
{
    boolean cnpjCpfEnabled  = false;
    boolean ieNumEnabled    = false;
    boolean ccmNumEnabled   = false;
    boolean nitEnabled      = false;
    boolean inssceiEnabled  = false;
    boolean cnaeEnabled     = false;

    // disable is default
    switch (lastPartyType)
    {
        case DirPartyType::Organization:
        case DirPartyType::OperatingUnit:
        case DirPartyType::LegalEntity:
            cnpjCpfEnabled  = true;
            ieNumEnabled    = true;
            ccmNumEnabled   = true;
            cnaeEnabled     = true;
            break;

        case DirPartyType::Person:
            cnpjCpfEnabled  = true;
            ccmNumEnabled   = true;
            nitEnabled      = true;
            inssceiEnabled  = true;
            break;

        case DirPartyType::None:
        case DirPartyType::Team:
        default:
            // all disabled
            break;
    }

    this.enableCnpjCpf_BR(cnpjCpfEnabled);
    this.enableIENum_BR(ieNumEnabled);
    this.enabledCCMNum_BR(ccmNumEnabled);
    this.enableNIT_BR(nitEnabled);
    this.enableINSSCEI_BR(inssceiEnabled);
    this.enableCNAE_BR(cnaeEnabled);
}

