public void init()
{
    #isoCountryRegionCodes

    super();

    if (!hasCachedDisplayMethodVendTable)
    {
        vendTable_ds.cacheAddMethod(tableMethodStr(VendTable, interCompanyTradingRelationActive));
        vendTable_ds.cacheAddMethod(tableMethodStr(VendTable, interCompanyTradingPartnerCompanyID));
        vendTable_ds.cacheAddMethod(tableMethodStr(VendTable, interCompanyTradingPartnerAccount));
        vendTable_ds.cacheAddMethod(tableMethodStr(VendTable, balanceAllCurrency));
        hasCachedDisplayMethodVendTable = true;
    }

    if (traceFilter)
    {
        traceFilter.parmMainDataSource(VendTable_ds);
        traceFilter.parmFilterDataSource(VendTableTmpFilter_ds);
        traceFilter.dataSourceInitPost();

        vendTable_ds.cacheAddMethod(tableMethodStr(VendTable, phone));
        vendTable_ds.cacheAddMethod(tableMethodStr(VendTable, phoneLocal));
        vendTable_ds.cacheAddMethod(tableMethodStr(VendTable, editContactPersonName));
        vendTable_ds.cacheAddMethod(tableMethodStr(VendTable, contactPersonPhone));
    }

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoMX]))
    {
        this.object(fieldNum(VendTable, VendorType_MX)).Mandatory(true);
        this.object(fieldNum(VendTable, OperationType_MX)).Mandatory(true);
    }
}

public void initValue()
{
    super();

    if (element.args())
    {
        callerRecord = element.args().record();

        if (callerRecord.TableId == tableNum(DirPartyTable))
        {
            dirPartyTable.data(callerRecord);

            if (dirPartyTable.type() == DirPartyType::Person)
            {
                dirPersonName.data(DirPersonName::find(dirPartyTable.RecId));
            }
            vendTable.Party = dirPartyTable.RecId;
            vendTable.modifiedField(fieldNum(VendTable,Party));
        }
    }
}

public int active()
{
    int ret;
    CatVendorChannel catVendorChannel;

    #ISOCountryRegionCodes

    ret = super();

    if (ret)
    {
        partyForm.parmPartyType(dirPartyTable.type());
        element.updateControls();
        element.updateDesign();

        if (VendTableVendorApprovalOrgNameOverriddenFlight::instance().isEnabled())
        {
            element.markChangeProposalFields();
        }

        // call to the method to enable/disable the Blocked Release Date according to the Blocked field
        element.toggleBlockedReleaseDate();
        // <GEERU>
        element.enableVATTaxAgent_RU();
        // </GEERU>
    }

    //Enable or Disable buttons for catalog import processing
    select firstonly RecId, IsEnabled, VendorParty from catVendorChannel where catVendorChannel.VendorParty == vendTable.Party;

    if (catVendorChannel.RecId == 0)
    {
        configureCatalogImportProcessing.visible(true);
        enableCatalogImportProcessing.visible(false);
        disableCatalogImportProcessing.visible(false);
    }
    else
    {
        if (catVendorChannel.IsEnabled == NoYes::Yes)
        {
            configureCatalogImportProcessing.visible(false);
            enableCatalogImportProcessing.visible(false);
            disableCatalogImportProcessing.visible(true);
        }
        else
        {
            configureCatalogImportProcessing.visible(false);
            enableCatalogImportProcessing.visible(true);
            disableCatalogImportProcessing.visible(false);
        }
    }

    if (SecurityRights::construct().fieldAccessRight(tableStr(VendTable), fieldStr(VendTable, MinorityOwned)) != AccessRight::NoAccess)
    {
        vendTable_ds.object(fieldNum(VendTable,EthnicOriginId)).enabled(vendTable.MinorityOwned == NoYes::Yes);
    }
    else
    {
        vendTable_ds.object(fieldNum(VendTable,EthnicOriginId)).visible(false);
    }

    this.initChangeRequestStatus();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoMX]))
    {
        element.enableRfcCurpFields_MX(vendTable);
        element.enableVendorTypeFields_MX(vendTable);
    }

    if (TaxWithholdingGlobalFeature::isItemWHTSupportedInCountryRegionOrParamEnabled())
    {
        withholdingTax_TaxWithholdGroup.enabled(vendTable.TaxWithholdCalculate);
    }
    // <GIN>
    if (countryRegionIN)
    {
        if (TaxParameters::find().Customs_IN)
        {
            element.setCustomsGroupVisible_IN();
        }

        if (!TaxParameters::find().Excise_IN)
        {
            vendTable_SSIValidityDate_IN.visible(false);
            vendTable_SSIValidityDate_IN.enabled(false);
            vendTable_SSIVendor_IN.visible(false);
            vendTable_SSIVendor_IN.enabled(false);
        }
        else
        {
            vendTable_SSIValidityDate_IN.visible(true);
            vendTable_SSIValidityDate_IN.enabled(true);
            vendTable_SSIVendor_IN.visible(true);
            vendTable_SSIVendor_IN.enabled(true);
        }

        element.setTaxWithholdEnabled_IN();
        taxInformationVendTable_IN_ds.object(fieldNum(TaxInformationVendTable_IN, panNumber)).enabled(taxInformationVendTable_IN.panStatus == PANStatus_IN::Received);
        taxInformationVendTable_IN_ds.object(fieldNum(TaxInformationVendTable_IN, panReferenceNumber)).enabled(taxInformationVendTable_IN.panStatus == PANStatus_IN::Applied);
    }
    // </GIN>

    if (ret &&
        BrazilParameters::isEnabled())
    {
        this.partyTypeModified_BR();
        vendTable_ds.enableForeignerId_BR();
    }

    // <GJP>
    if (countryRegionJP)
    {
        vendTable_ds.object(fieldnum(VendTable, VendPaymFeeGroup_JP)).enabled(VendTable.isPaymFeeCovered_JP == NoYes::Yes);
    }
    // </GJP>

    // Show/hide view proposal details button
    VendTableChangeProposal proposal = VendTableChangeProposal::findByVendRecId(VendTable.RecId, false, true);
    VendChangeProposal.visible(VendTable.RecId != 0 && proposal.RecId != 0);
    // workflow state display
    VendTable_WorkflowState.visible(VendTable.RecId != 0 && proposal.RecId != 0);

    element.updateVendorChargedSalesTaxToleranceController();

    return ret;
}

public boolean validateWrite()
{
    // Since the account number generation might be delayed until save, we will handle it here.
    // We will generate a new account if the account number is empty with a vendor group.
    if (VendTable.AccountNum == '' &&
        VendTable.VendGroup)
    {
        element.numberSeqFormHandler().formMethodDataSourceCreate(true);
    }

    boolean ret = super();

    if (ret)
    {
        ret = element.numberSeqFormHandler().formMethodDataSourceValidateWrite(ret);
    }

    if (ret)
    {
        ret = partyForm.validateWrite();
    }

    // <GTH>
    if (ret && TaxThaiGovCertificationFeatureChecker::isTaxBranchEnabled())
    {
        ret = TaxBranchType::checkTaxBranchByDefaultDimension(vendTable.DefaultDimension);
    }
    // </GTH>

    // Validate all the visible controls, this is for copy vendor feature
    int fieldCount = 1;

    QueryBuildFieldList fieldList = vendTable_ds.query().dataSourceTable(tablenum(VendTable)).fields();

    while (fieldCount <= fieldList.fieldCount())
    {
        DictField dictField = new DictField(tablenum(VendTable), fieldList.field(fieldCount));
        int dictFieldNum = dictField.id();

        if (dictFieldNum != fieldNum(VendTable, AccountNum) &&
            dictFieldNum != fieldNum(VendTable, VendGroup))
        {
            FormDataObject control = vendTable_ds.object(dictFieldNum);

            if (control != null && control.visible())
            {
                if (!control.validate())
                {
                    ret = false;
                }
            }
        }

        fieldCount++;
    }

    return ret;
}

public void linkActive()
{
    smmResponsibilityGroup  responsibilityGroup;
    VendTable               vendTableLocal;
    VendInvoiceInfoTable    vendInvoiceInfoTable;
    LedgerJournalTrans      ledgerJournalTrans;
    FormRun                 callingForm;

    element.numberSeqFormHandler().formMethodDataSourceLinkActive();

    // Is form called with a record
    if (element.args() && element.args().record())
    {
        callingForm = element.args().caller() As FormRun;

        switch (element.args().dataset())
        {
            // Is the form called from the contact person form
            case tableNum(ContactPerson) :
                this.query().dataSourceTable(tableNum(VendTable)).clearDynalinks();
                this.query().dataSourceTable(tableNum(VendTable)).addDynalink(fieldNum(VendTable,Party), element.args().record(), fieldNum(ContactPerson,ContactForParty));
                break;

            // Is the form called from the activities form
            case tableNum(smmActivities) :
                smmActivityParentLink::updateQuery(this.query().dataSourceTable(tableNum(VendTable)), element.args().record());
                break;

            // Is the form called from the responsibilities form
            case tableNum(smmResponsibilityGroup) :
                // Get the current selected responsibilities record
                responsibilityGroup  = element.args().record();

                // Update query
                smmResponsibility::updateQuery(this.query(),this.table(),responsibilityGroup.ResponsibilityId);
                break;

            //Is the form called from the PurchEditLines form
            case tableNum(PurchParmTable) :
                this.query().dataSourceTable(tableNum(VendTable)).clearDynalinks();
                this.query().dataSourceTable(tableNum(VendTable)).addDynalink(fieldNum(VendTable,AccountNum), element.args().record(), fieldNum(PurchParmTable,InvoiceAccount));
                break;

            //Is the form called from the VendEditInvoice form
            case tableNum(VendInvoiceInfoTable) :
                this.query().dataSourceTable(tableNum(VendTable)).clearDynalinks();
                this.query().dataSourceTable(tableNum(VendTable)).addDynalink(fieldNum(VendTable,AccountNum), element.args().record(), fieldNum(VendInvoiceInfoTable,InvoiceAccount));
                break;

            case tableNum(RetailInventTable) :
                this.query().dataSourceTable(tableNum(VendTable)).clearDynalinks();
                break;
        }
    }

    //VendInvoiceInfoListPage requires that all vendors show in the list
    if (callingForm && (callingForm.name() == formStr(VendInvoiceInfoListPage)))
    {
        this.query().dataSourceTable(tableNum(VendTable)).clearRanges();
    }

    super();

    //VendInvoiceInfoListPage requires that whichever vendor is selected on the list page must
    // be selected
    if (callingForm)
    {
        switch (callingForm.name())
        {
            case formStr(VendInvoiceInfoListPage) :
                vendInvoiceInfoTable = element.args().record();
                vendTableLocal = VendTable::find(vendInvoiceInfoTable.InvoiceAccount);
                this.positionToRecord(vendTableLocal);
                this.mark(1);
                break;
        }
    }
}

void write()
{
    element.numberSeqFormHandler().formMethodDataSourceWrite();

    vendTable.Party = dirPartyTable.RecId;

    // <GIN>
    if (countryRegionIN)
    {
        taxInformationVendTable_IN.VendTable = vendTable.AccountNum;
        taxInformationVendTable_IN.write();
    }
    // </GIN>
    super();
    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)) && !retailVendTable)
    {
        retailVendTable.write();
    }
}

void delete()
{
    FormDataSource callerDataSource;

    element.numberSeqFormHandler().formMethodDataSourceDelete();

    super();

    if (element.args() && element.args().dataset() == tableNum(smmBusRelTable))
    {
        callerDataSource = FormDataUtil::getFormDataSource(element.args().record());
        if (callerDataSource)
        {
            callerDataSource.executeQuery();
        }
    }
}

void create(boolean append = false,
            boolean extern = false)  // If created externally
{
    element.numberSeqFormHandler().formMethodDataSourceCreatePre();

    super(append);

    if (!element.isDelayNumberSequenceGeneration())
    {
        if (!extern)
        {
            element.numberSeqFormHandler().formMethodDataSourceCreate();
        }
    }
    else
    {
        VendTable vendor = VendTable_ds.cursor();

        // If number sequence generation will be delayed, vendor account number control will be enabled/disabled base on if number sequence is manual.
        if (!element.isNumberSequenceManual())
        {
            info("@AccountsPayable:VendAccountNumberGenerationDelay");
            VendTable_ds.object(fieldnum(VendTable, AccountNum)).allowedit(false);
        }
        else
        {
            VendTable_ds.object(fieldnum(VendTable, AccountNum)).allowedit(true);
        }

    }
}

display boolean listExist(VendTable _vendTable)
{
    return VendDefaultAccounts::existMoreOnVendAccount(_vendTable.AccountNum);
}

private void initChangeRequestStatus()
{
    if (!PurchParameters::find().ChangeRequestAllowOverride)
    {
        this.object(fieldNum(VendTable, ChangeRequestOverride)      ).allowEdit(false);
        this.object(fieldNum(VendTable, ChangeRequestEnabled)       ).allowEdit(false);
        this.object(fieldNum(VendTable, ChangeRequestAllowOverride) ).allowEdit(false);
    }
    else
    {
        this.object(fieldNum(VendTable, ChangeRequestOverride)      ).allowEdit(true);
        this.object(fieldNum(VendTable, ChangeRequestEnabled)       ).allowEdit(vendTable.ChangeRequestOverride);
        this.object(fieldNum(VendTable, ChangeRequestAllowOverride) ).allowEdit(vendTable.ChangeRequestOverride);
    }
}

public void enableForeignerId_BR()
{
    this.object(fieldNum(VendTable, ForeignerId_BR)).allowEdit(vendTable.isForeigner_BR());
}

public void research(boolean _retainPosition = false)
{
    if (VendTableDontRetainPositionOnResearchPerfEnhancement::instance().isEnabled())
    {
        VendTable vendTableCursor;

        if (_retainPosition)
        {
            vendTableCursor.data(VendTable_ds.cursor());
        }

        super(false);

        if (_retainPosition)
        {
            // positionToRecord is not optimal for a large resultset because kernel has to execute paged queries when scrolling beyond cached set
            // findRecord is considered as obsolete API however has no paging overhead
            VendTable_ds.findRecord(vendTableCursor);
        }
    }
    else
    {
        super(_retainPosition);
    }
}

public void modified()
{
    element.updateVendorChargedSalesTaxToleranceController();
    if (VendTable.AccrueSalesTaxType == TaxAccrueSalesTaxType::Advanced)
    {
        TaxIntegrationTaxServiceParameters taxIntegrationTaxServiceParameters = TaxIntegrationTaxServiceParameters::find();
        vendTable.TaxVendorChargeTaxToleranceAmount = taxIntegrationTaxServiceParameters.ToleranceAmount;
        vendTable.TaxVendorChargeTaxTolerancePercent = taxIntegrationTaxServiceParameters.TolerancePercent;
        vendTable.TaxVendorChargeTaxToleranceValidation = taxIntegrationTaxServiceParameters.ToleranceValidation;
    }
    super();
}

public void lookup(FormControl _formControl, str _filterStr)
{
    // Note: vendTable.dataAreaId might be a virtual company.
    // We cannot look up vendor bank account in a virtual company.
    // Therefore, use company() method on the table buffer
    // to get vendor bank account from current concrete company.
    VendBankAccount::lookupBankAccount(_formControl,
                                        vendTable.AccountNum,
                                        vendTable.company(),
                                        DateTimeUtil::utcNow(),
                                        vendTable.PaymMode);
}

