public void modified()
{
    if (VendTable.isPaymFeeCovered_JP == NoYes::Yes)
    {
        vendTable_ds.object(fieldnum(VendTable, VendPaymFeeGroup_JP)).enabled(true);
    }
    else
    {
        vendTable.VendPaymFeeGroup_JP = "";
        vendTable_ds.object(fieldnum(VendTable, VendPaymFeeGroup_JP)).enabled(false);
    }

    super();
}

public void modified()
{
    super();
    // Remove value from Ethnic Origin if Minority owned is not selected
    unchecked(Uncheck::TableSecurityPermission)
    {
        if (vendTable.MinorityOwned == NoYes::No)
        {
            vendTable.EthnicOriginId = '';
        }
    }
    // enable Ethnic Origin field only if Minority owned is selected
    vendTable_ds.object(fieldNum(VendTable,EthnicOriginId)).enabled(vendTable.MinorityOwned == NoYes::Yes);
}

/// <summary>
/// Refreshes the data source <c>VendTable</c> table of the underlying form.
/// </summary>
public void modified()
{
    element.modifiedControlledField(
    fieldNum(VendTable, PaymMode),
    fieldNum(VendTableChangeProposal, PaymMode),
    VendTable,
