public void lookup(FormControl _formControl, str _filterStr)
{
    // <GIN>

    // Parameter checking cannot be included since lookup should list only values of tax type 'None',
    // when the Activate TDS or Activate TCS is not marked. If there is a super call, then all the
    // component groups will be displayed.
    if (TaxWithholdParameters_IN::checkTaxParameters())
    {
        TaxWithholdGroupHeading::lookupTaxComponentGroup_IN(_formControl, TaxWithholdCodeType_IN::None);
    }
    // </GIN>
    else
    {
        super(_formControl, _filterStr);
    }
}

