public void modified()
{                
    isVendGroupValueSetOnce = false;

    if (VendDefaultAccounts::existVendAccount(vendTable.AccountNum))
    {
        DialogButton dialogButton = Box::yesNo(strFmt("@SYS99305", vendTable.AccountNum), DialogButton::Yes);
        if (dialogButton == DialogButton::Yes)
        {
            VendDefaultAccounts vendDefaultAccounts;
            delete_from vendDefaultAccounts
                where vendDefaultAccounts.VendAccount == vendTable.AccountNum;
        }
    }

    boolean isValueModified = VendTable::confirmAndSaveVendGroupChange(VendTable, isVendGroupValueSetOnce);

    if (!isValueModified && vendTable.VendGroup != vendTable.orig().VendGroup)
    {
        isValueModified = true;
    }

    if (isValueModified)
    {
        isVendGroupValueSetOnce = true;

        element.modifiedControlledField(
            fieldNum(VendTable, VendGroup),
            fieldNum(VendTableChangeProposal, VendGroup),
            VendTable,
            VendTableChangeProposalField::VendGroup);
    }

    super();
}

