public void modified()
{
    // Change control
    DirPartyName originalName = DirPartyTable.orig().Name;
    DirPartyName newName = DirPartyTable.Name;

    if (originalName != newName
        && VendTableChangeProposalFieldEnablement::isEnabledFor(VendTableChangeProposalField::Name))
    {
        if (!vendTable)
        {
            element.showWarningNoChangeControlOnCreation();
        }
        else
        {
            DirPartyTable.Name = originalName;

            if (VendTable.canModifyChangeProposal())
            {
                // Create or update an approval entry
                ttsbegin;
                VendTableChangeProposal change = VendTableChangeProposal::findOrCreate(vendTable.RecId, true);
                change.Name = newName;
                change.IsChangedName = NoYes::Yes;
                change.update();
                ttscommit;
            }

            // pop dialog
            element.showChangeProposalDialog();
        }
    }

    super();

    if (!vendTable.RecId && dirPartyTable.RecId)
    {
        partyForm.modifiedName();
        vendTable.Party = dirPartyTable.RecId;

        // Perform a manual refresh of the DirPartyTable data source to ensure
        // all party fields have been bound and updated correctly.
        DirPartyTable partyTable = DirPartyTable::findRec(dirPartyTable.RecId);
        dirPartyTable.data(partyTable);

        // <GBR>
        if (BrazilParameters::isEnabled())
        {
            vendTable.modifiedField(fieldNum(VendTable, Party));
            vendTable_ds.refresh();
        }
        // </GBR>

        element.updateControls();
    }
}

