public boolean validateWrite()
{
    boolean ret = true;

    if (zakatParameterEnabled)
    {
        if (vendInfoZakat_SA.VendAccount == '')
        {
            vendInfoZakat_SA.VendAccount = vendTable.AccountNum;
        }
        ret = super();
    }

    // Validate all the visible controls, this is for copy vendor feature
    DictField dictField;
    int fieldCount = 1;

    QueryBuildFieldList fieldList = VendInfoZakat_SA_ds.query().dataSourceTable(tablenum(VendInfoZakat_SA)).fields();

    while (fieldCount <= fieldList.fieldCount())
    {
        dictField = new DictField(tablenum(VendInfoZakat_SA), fieldList.field(fieldCount));
        str fieldName = DictField.name();

        if (dictField.id() != fieldNum(VendInfoZakat_SA, VendAccount))
        {
            FormDataObject control = VendInfoZakat_SA_ds.object(dictField.id());

            if (control != null && control.visible())
            {
                if (!control.validate())
                {
                    ret = false;
                }
            }
        }

        fieldCount++;
    }

    return ret;
}

