public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    SysReferenceTableLookup sysTableLookup;
    Query                   query = new Query();
    QueryBuildDataSource    qbds;
    QueryBuildDataSource    qbds1;

    qbds    = query.addDataSource(tableNum(EcoResCategoryHierarchy));
    qbds1 = qbds.addDataSource(tableNum(EcoResCategoryHierarchyRole));
    qbds1.relations(true);
    qbds1.addRange(fieldNum(EcoResCategoryHierarchyRole, NamedCategoryHierarchyRole)).value(queryValue(EcoResCategoryNamedHierarchyRole::RetailVendorProductHierarchy));

    sysTableLookup  = SysReferenceTableLookup::newParameters(tableNum(EcoResCategoryHierarchy), _formReferenceControl);
    sysTableLookup.addLookupfield(fieldNum(EcoResCategoryHierarchy, Name));
    sysTableLookup.parmQuery(query);

    return sysTableLookup.performFormLookup();
}

public boolean validateWrite()
{
    boolean ret;
    ret = super();

    if (ret && TaxWithholdParameters_IN::checkTaxParameters())
    {
        if (vendTable.TaxWithholdCalculate
            && !vendTable.TaxWithholdGroup
            && !taxInformationVendTable_IN.tdsGroup
            && !taxInformationVendTable_IN.tcsGroup)
        {
            ret = checkFailed("@GLS5850");
        }
    }

    // Validate all the visible controls, this is for copy vendor feature
    DictField dictField;
    int fieldCount = 1;

    QueryBuildFieldList fieldList = TaxInformationVendTable_IN_ds.query().dataSourceTable(tablenum(TaxInformationVendTable_IN)).fields();

    while (fieldCount <= fieldList.fieldCount())
    {
        dictField = new DictField(tablenum(TaxInformationVendTable_IN), fieldList.field(fieldCount));

        if (dictField.id() != fieldNum(TaxInformationVendTable_IN, VendTable))
        {
            FormDataObject control = TaxInformationVendTable_IN_ds.object(dictField.id());
            if (control != null && control.visible())
            {
                if (!control.validate())
                {
                    ret = false;
                }
            }
        }

        fieldCount++;
    }

    return ret;
}

