public boolean validateWrite()
{
    #ISOCountryRegionCodes
    boolean ret = true;
    // Make this data source read only if respective localization is not active.
    if (!SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        ret = false;
    }

    if (ret && !dirPartyTable.RecId)
    {
        if (dirPartyTable_ds.validateWrite())
        {
            dirPartyTable_ds.write();
        }
        else
        {
            ret = false;
        }
    }

    cfmDirPartyTable.DirPartyTable = dirPartyTable.RecId;

    ret = ret && super();

    return ret;
}

public void init()
{
    #ISOCountryRegionCodes
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
