void clicked()
{
    Args args;
    VendTable myVendTable;

    myVendTable = VendTable::find(vendTable.AccountNum);

    args = new Args();
    args.record(myVendTable);

    new MenuFunction(menuitemDisplayStr(ForecastPurchTableAction), MenuItemType::Display).run(args);
}

public void clicked()
{
    VendTrans vendTransLoc;
    VendInvoiceJour vendInvoiceJourLoc;

    super();

    select firstonly RecId from vendTransLoc
        where vendTransLoc.SettleAmountCur != 0
            && vendTransLoc.AmountCur != vendTransLoc.SettleAmountCur
            && vendTransLoc.AccountNum == vendTable.AccountNum
        join vendInvoiceJourLoc 
            where vendInvoiceJourLoc.OrderAccount == vendTransLoc.AccountNum
                && vendInvoiceJourLoc.InvoiceId == vendTransLoc.Invoice
                && vendInvoiceJourLoc.InvoiceType == PurchInvoiceType::VendorAdvance;

    if (vendTransLoc.RecId)
    {
        warning("@AccountsPayable:VendInvoicePrePaymentExists");
    }
}

void clicked()
{
    PrintMgmtSetupContext setupContext = new PrintMgmtSetupContext();

    if (vendTable_ds.cursor() == null)
    {
        return;
    }

    super();

    setupContext.addHierarchyContext(PrintMgmtHierarchyType::Purch, PrintMgmtNodeType::VendTable);
    setupContext.parmCaller(element);
    setupContext.parmReferencedTableBuffer(vendTable_ds.cursor());
    setupContext.parmCountryRegionId(vendTable.postalAddress().CountryRegionId);
    PrintMgmt::launchSetup(setupContext);
}

public void clicked()
{
