    {
        super();
    }
}

public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        DirPartyType selected = any2Enum(typeComboBox.selection());

        if (selected != lastPartyType)
        {
            vendTable_ds.delete();
            vendTable_ds.research();
            partyForm.parmPartyType(selected);
            partyForm.create();
            lastPartyType = selected;
        }
    }

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        vendTable_ds.partyTypeModified_BR();
    }
    // </GBR>

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    VendTable vendor = VendTable_ds.cursor() as VendTable;
    VendGroup vGroup = VendGroup::find(vendor.VendGroup);

    // Account number can be changed when creating a new record without copying.
    if (!vendor.RecId && element.isDelayNumberSequenceGeneration() && !isCopied)
    {
        if (element.isNumberSequenceManual())
        {
            VendTable_ds.object(fieldnum(VendTable, AccountNum)).allowedit(true);
        }
        else
        {
            VendTable_ds.object(fieldnum(VendTable, AccountNum)).setValue('');
            VendTable_ds.object(fieldnum(VendTable, AccountNum)).allowedit(false);
        }
    }

    return ret;
}

public void jumpRef()
{
    super();
    smmUtility::openMenuItemForm(menuitemDisplayStr(DirAddressBook), null, this);
}

public boolean modified()
{
    boolean ret;

    // Write data source before writing address books to party.
    dirPartyTable_ds.write();
    ret = super();

    if (ret)
    {
        boolean forceWrite = vendTable_ds.forceWrite();
        // Set forceWrite to make form handler write address books to party record.
        vendTable_ds.forceWrite(true);
        partyForm.writeAddressBookParty();
        vendTable_ds.forceWrite(forceWrite);
        // Reread data source to reload the buffer with the changed address book associations.
        dirPartyTable_ds.reread();
    }

    return ret;
}

public void jumpRef()
{
    super();

    logisticsElectronicAddress.performTypeLookup(contactInfo_Locator);
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (this.value() == true && logisticsElectronicAddress.otherPrimaryExists())
        {
            if (Box::yesNo("@SYS304427", DialogButton::No) == DialogButton::No)
            {
                ret = false;
            }
        }
    }

    return ret;
}

public boolean validate()
{
    boolean valid = super();

    if (valid)
    {
        valid = CustTable::checkExist(this.text());
    }

    return valid;
}

public void jumpRef()
{
    Args args = new Args();
    args.record(vendTable);
    args.parmEnum(NoYes::Yes); // It's a Go To Main Table call

    args.lookupRecord(ContactPerson::find(vendTable.ContactPersonId));
    new MenuFunction(menuitemDisplayStr(ContactPerson), MenuItemType::Display).run(args);
}

