private void refreshDataSources()
{
    vendCategory_ds.research();
    dirPartyEcoResCategory_ds.research();
}

public void init()
{        
    if (!element.args().record() || element.args().record().TableId != tableNum(VendTable))
    {
        throw error(error::missingRecord(tableId2PName(tableNum(VendTable))));
    }
    super();        

    // Hide attach button
    #SysSystemDefinedButtons

    FormCommandButtonControl attachButton = this.control(this.controlId(#SystemDefinedAttachButton)) as FormCommandButtonControl;

    if (attachButton)
    {
        attachButton.visible(false);
    }

    VendRequestManagementParameters vendorRequestManagementParameters = VendRequestManagementParameters::find();
    boolean isVendorCollaborationCertificationManagementEnabled = VendorCollaborationCertificationManagementFlight::instance().isEnabled()
        ? vendorRequestManagementParameters.IsVendorCollaborationCertificationManagementEnabled
        : FeatureStateProvider::isFeatureEnabled(VendorCertificationManagementFeature::instance());

    if (isVendorCollaborationCertificationManagementEnabled)
    {
        NewCertification.visible(true);

        DeleteCertification.visible(true);

        VendCertification_Reviewed.visible(true);
    }

    this.initializePartyFormHandler();

    boolean isVendorCollaborationWorkspaceBankInformationEnabled = VendorCollaborationWorkspaceBankInformationFlight::instance().isEnabled()
        ? vendorRequestManagementParameters.IsVendorCollaborationWorkspaceBankInformationEnabled
        : true;

    if(!isVendorCollaborationWorkspaceBankInformationEnabled)
    {
        TabBankInformation.visible(false);
    }
    else
    {
        VendBankAccount.hasDocuments();
    }

    DocuAttachmentCertification.visible(true);
    AttachmentCommandButton.visible(false);
    DocuAttachmentBankAccount.visible(true);
    AddDocuments.visible(false);

    AddtoOutlookContacts.visible(!smmOutlookSyncDeprecationHandler::isOutlookSyncDisabled());
}

private void initializePartyFormHandler()
{
    partyForm = new DirPartyFormHandler_Entity(element);
    partyForm.setDatasourceCaller(vendTableProfile_ds);
    partyForm.setDatasourceParty(dirPartyTable_ds);
    partyForm.setDatasourcePartyName(dirPersonName_ds);
    partyForm.ctrlTabPageGeneral(tabGeneral);
    partyForm.setNameControls(VendProfileOrganizationDetails_OrgNumber, name_FirstName, name_MiddleName, name_LastName);

}

private void setVendorProfileFieldsConfiguration(VendorInformationFormConfiguration _vendorInformationFormConfiguration)
{        
    VendProfile_LocallyOwned.allowEdit(_vendorInformationFormConfiguration.LocallyOwnedFieldAccessLevel == HideViewEdit::Edit);
    VendProfile_SmallBusiness.allowEdit(_vendorInformationFormConfiguration.SmallBusinessFieldAccessLevel == HideViewEdit::Edit);
    VendProfile_FemaleOwned.allowEdit(_vendorInformationFormConfiguration.FemaleOwnedFieldAccessLevel == HideViewEdit::Edit);
    VendProfile_MinorityOwned.allowEdit(_vendorInformationFormConfiguration.MinorityOwnedFieldAccessLevel == HideViewEdit::Edit);
    VendProfile_VeteranOwned.allowEdit(_vendorInformationFormConfiguration.VeteranOwnedFieldAccessLevel == HideViewEdit::Edit);
    VendProfile_DisabledOwned.allowEdit(_vendorInformationFormConfiguration.DisabledOwnedFieldAccessLevel == HideViewEdit::Edit);
    VendProfile_HUBZone.allowEdit(_vendorInformationFormConfiguration.HUBZoneFieldAccessLevel == HideViewEdit::Edit);
    this.enableDisableEthnicOriginId();

    VendProfile_LocallyOwned.visible(_vendorInformationFormConfiguration.LocallyOwnedFieldAccessLevel != HideViewEdit::Hide);
    VendProfile_SmallBusiness.visible(_vendorInformationFormConfiguration.SmallBusinessFieldAccessLevel != HideViewEdit::Hide);
    VendProfile_FemaleOwned.visible(_vendorInformationFormConfiguration.FemaleOwnedFieldAccessLevel != HideViewEdit::Hide);
    VendProfile_MinorityOwned.visible(_vendorInformationFormConfiguration.MinorityOwnedFieldAccessLevel != HideViewEdit::Hide);
    VendProfile_VeteranOwned.visible(_vendorInformationFormConfiguration.VeteranOwnedFieldAccessLevel != HideViewEdit::Hide);
    VendProfile_DisabledOwned.visible(_vendorInformationFormConfiguration.DisabledOwnedFieldAccessLevel != HideViewEdit::Hide);
    VendProfile_HUBZone.visible(_vendorInformationFormConfiguration.HUBZoneFieldAccessLevel != HideViewEdit::Hide);
    VendProfile_EthnicOriginId.visible(_vendorInformationFormConfiguration.EthnicOriginIdFieldAccessLevel != HideViewEdit::Hide);
}

private void enableDisableEthnicOriginId(VendorInformationFormConfiguration _vendorInformationFormConfiguration = VendorInformationFormConfiguration::find())
{
    VendProfile_EthnicOriginId.allowEdit(
        _vendorInformationFormConfiguration.EthnicOriginIdFieldAccessLevel == HideViewEdit::Edit 
        && VendTableProfile.MinorityOwned);
}

