private void setTabFocus()
{
    switch (element.args().parm())
    {
        case (enum2Str(VendTableVendExternalPage::General)):
            TabGeneral.setFocus();
            break;

        case (enum2Str(VendTableVendExternalPage::Address)):
            TabAddress.setFocus();
            break;

        case (enum2Str(VendTableVendExternalPage::Contacts)):
            TabCommunication.setFocus();
            break;

        case (enum2Str(VendTableVendExternalPage::Business)):
            TabBusinessInformation.setFocus();
            break;

        case (enum2Str(VendTableVendExternalPage::SalesTaxId)):
            TabStateTaxId.setFocus();
            break;

        case (enum2Str(VendTableVendExternalPage::Categories)):
            TabProcurement.setFocus();
            break;

        case (enum2Str(VendTableVendExternalPage::Certifications)):
            TabCertifications.setFocus();
            break;

        case (enum2Str(VendTableVendExternalPage::BankInformation)):
            TabBankInformation.setFocus();
            break;
    }
}

public void updateDesign()
{
    if (dirPartyTable)
    {
        DirPartyType partyType = dirPartyTable.type();
        boolean      isOrganization = partyType == DirPartyType::Organization ? true : false;

        OrgDetailGroup.visible(isOrganization);

        DirPartyTable_KnownAs.visible(isOrganization);                        
        DirPartyTable_DirOrganizationBase_PhoneticName.visible(isOrganization);
        DirPartyTable_DirOrganizationBase_DunsNumberRecId.visible(isOrganization);

        PersonGroup.visible(partyType == DirPartyType::Person);

        element.setVendorProfileFieldsConfiguration(VendorInformationFormConfiguration::find());
    }
}

