public void init()
{
    super();
    this.queryBuildDataSource().addDynalink(fieldNum(DirPartyLocation, Party), dirPartyTable, fieldNum(DirPartyTable, RecId));
    this.queryBuildDataSource().addLink(fieldNum(LogisticsElectronicAddress, Location), fieldNum(DirPartyLocation, Location));
    this.queryBuildDataSource().addRange(fieldNum(DirPartyLocation, IsPostalAddress)).value(queryValue(NoYes::No));
}

public boolean validateWrite()
{
    boolean isValid;

    // Location is mandatory but it gets created only on write
    // Store a temporary location since we don't want to skip validation totally
    if (!logisticsElectronicAddress.Location)
    {
        logisticsElectronicAddress.Location = 1;
    }

    isValid = super();

    // Reset temporary location
    if (logisticsElectronicAddress.Location == 1)
    {
        logisticsElectronicAddress.Location = 0;
    }

    return isValid;
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        logisticsElectronicAddress.enableFields();                
    }

    DetailContactInfo.enabled(logisticsElectronicAddress.RecId);

    return ret;
}

public void init()
{
    super();

    this.queryBuildDataSource().addOrderByField(fieldNum(LogisticsElectronicAddress, IsPrimary), SortOrder::Descending);
}

public void initValue()
{
    super();
    logisticsElectronicAddress.Location = contactInfoLocation;
}

public void write()
{
    ttsbegin;
    partyForm.electronicAddressPreWrite(logisticsElectronicAddress);
    super();
    partyForm.electronicAddressPostWrite(logisticsElectronicAddress);
    ttscommit;

    contactInfoLocation = logisticsElectronicAddress.Location;
}

public void executeQuery()
{
    super();

    if (contactInfoRecId != 0 && contactInfoRecId != logisticsElectronicAddress.RecId)
    {
        this.positionToRecordByValue(fieldNum(LogisticsElectronicAddress,RecId), SysQuery::value(contactInfoRecId));
    }
    contactInfoLocation = logisticsElectronicAddress.Location;
}

public void delete()
{
    // Vendor to category relation should be expired
    dirPartyEcoResCategory.expireActiveRecord(dirPartyEcoResCategory.Party, dirPartyEcoResCategory.Category);
    element.refreshDataSources();
}

public void executeQuery()
{
    QueryBuildRange qbrDirParty = SysQuery::findOrCreateRange(
        this.query().dataSourceTable(tableNum(DirPartyEcoResCategory)),
        fieldNum(DirPartyEcoResCategory, Party));

    qbrDirParty.value(SysQuery::value(vendTableProfile.Party));

    super();
}

public void executeQuery()
{
    if (vendTableProfile.RecId)
    {
        QueryBuildRange qbrVendAccount = SysQuery::findOrCreateRange(
            this.query().dataSourceTable(tableNum(VendCategory)), 
            fieldNum(VendCategory, VendorAccount));
        QueryBuildRange qbrLegalEntity = SysQuery::findOrCreateRange(
            this.query().dataSourceTable(tableNum(VendCategory)), 
            fieldNum(VendCategory, VendorDataArea));

        qbrVendAccount.value(SysQuery::value(vendTableProfile.AccountNum));
        qbrLegalEntity.value(SysQuery::value(vendTableProfile.DataAreaId));
    }

    super();
}

/// <summary>
/// Applies effective date filter selected.
/// </summary>
/// <param name="_filterOptions">
/// Date filter option parameter.
/// </param>
/// <param name="_showAsOfDate">
/// Effective date for a filter.
/// </param>
public void applyFilter(DateFilterOptions _filterOptions, TransDate _showAsOfDate)
{
    switch (_filterOptions)
    {
        case DateFilterOptions::AsOf:
            vendCategory_ds.validTimeStateAutoQuery(ValidTimeStateAutoQuery::AsOfDate);
            vendCategory_q.resetValidTimeStateQueryType();
            break;

        case DateFilterOptions::AsOfAndRange:
            vendCategory_ds.validTimeStateAutoQuery(ValidTimeStateAutoQuery::AsOfDate);
            vendCategory_q.validTimeStateAsOfDate(_showAsOfDate);
            break;

        case DateFilterOptions::Range:
            vendCategory_ds.validTimeStateAutoQuery(ValidTimeStateAutoQuery::DateRange);
            vendCategory_q.validTimeStateDateRange(dateNull(), maxDate());
            break;
    }

    vendCategory_ds.executeQuery();
}

