    vendCertification.CertificationOrigin = CertificationOrigin::Vendor;
    VendCertification.Reviewed = CertificationReviewed::No;
    super();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        // Remove value from Ethnic Origin if Minority owned is not selected
        unchecked(Uncheck::TableSecurityPermission)
        {
            if (vendTableProfile.MinorityOwned == NoYes::No)
            {
                vendTableProfile.EthnicOriginId = '';
            }
        }            

        element.enableDisableEthnicOriginId();
    }

    return ret;
}

public boolean validate()
{
    boolean isValid;

    isValid = super();

    if (isValid)
    {
        if (this.value() == true && logisticsElectronicAddress.otherPrimaryExists())
        {
            if (Box::yesNo("@SYS304427", DialogButton::No) == DialogButton::No)
            {
                isValid = false;
            }
        }
    }

    return isValid;
}

public void clicked()
{
    if (Box::yesNo("@SCM:WarningMessageForVendorExpiration", DialogButton::No) == DialogButton::Yes)
    {
        super();

        vendProcurementCategoryStatus.delete(); 

        element.refreshDataSources();               
    }
}

public void clicked()
{
    super();

    ShowAsOfDateControlProcurement.dateValue(dateNull());
    ShowAsOfDateControlProcurement.allowEdit(!this.value());

    // If checkbox is marked, we only show the current active records; if false, we show all.
    if (this.value())
    {
        VendCategory_ds.applyFilter(DateFilterOptions::AsOf, dateNull());
    }
    else
    {
        VendCategory_ds.applyFilter(DateFilterOptions::Range, dateNull());
    }
}

public boolean modified()
{
    boolean isModified;

    isModified = super();

    // If we select a date, we only show the records as of that date; if date control is empty, we show all.            
    if (isModified && this.dateValue())
    {
        VendCategory_ds.applyFilter(DateFilterOptions::AsOfAndRange, this.dateValue());
    }
    else if (isModified)
    {
        VendCategory_ds.applyFilter(DateFilterOptions::Range, dateNull());
    }

    return isModified;
}

public boolean modified()
{
    boolean isModified = super();

    if (isModified && VendCertification.orig().ValidFromDate != VendCertification.ValidFromDate)
    {
        VendCertification.Reviewed = CertificationReviewed::No;
    }

    return isModified;
}

public boolean modified()
{
    boolean isModified = super();

    if (isModified && VendCertification.orig().ValidToDate != VendCertification.ValidToDate)
    {
        VendCertification.Reviewed = CertificationReviewed::No;
    }

    return isModified;
}
