private boolean shouldJoinCountrySpecificDetails()
{
    boolean shouldJoinCountrySpecificDetails;

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU, #isoIN, #isoJP, #isoGB])
        || FeatureStateProvider::isFeatureEnabled(SplitPaymentMandatoryRuleFeature_PL::instance()))
    {
        shouldJoinCountrySpecificDetails = true;
    }

    if (!shouldJoinCountrySpecificDetails && filterHelper)
    {
        Set countryRegionCodes = filterHelper.getCountryRegionCodes();

        if (countryRegionCodes.in(#isoRU)
            || countryRegionCodes.in(#isoIN)
            || countryRegionCodes.in(#isoJP)
            || countryRegionCodes.in(#isoGB))
        {
            shouldJoinCountrySpecificDetails = true;
        }
    }

    return shouldJoinCountrySpecificDetails;
}

void parmNewQuery(Query _query)
{
    Query tmpQuery;

    tmpQuery = new Query(oldQuery);
    SysQuery::mergeRanges(tmpQuery, new Query(_query));

    this.query(tmpQuery);
    criteriaOpen = this.query().dataSourceNo(1).findRange(fieldNum(VendTrans,Closed));
    transTypeRange = this.query().dataSourceNo(1).findRange(fieldnum(VendTrans, TransType));
}

void init()
{
    Query query;
    // <GEERU>
    #isoCountryRegionCodes
    QueryBuildDataSource qdbs;
    QueryBuildDataSource qdbs_W;
    // </GEERU>
    // <GIN>
    #define.TaxWithholdTransTaxAdjustment('TaxWithholdTransTaxAdjustment')
    // </GIN>

    super();

    if (common.TableId == tableNum(TmpAccountSum))
    {
        globalCache = classfactory.globalCache();
        if (! globalCache.get(formStr(VendTrans),curUserId(),null))
        {
            globalCache.set(formStr(VendTrans),curUserId(), this);
            oldQuery = new Query(this.query());
        }
    }
    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]) && callerFacture)
    {
        this.query().dataSourceTable(tableNum(VendTrans)).addRange(fieldNum(VendTrans, RecId)).value(callerFacture.transRange());
    }
    // </GEERU>

    if (element.args().parmObject())
    {
        query = new Query(element.args().parmObject());

        //<GEERU>
        if (element.args().parmEnumType() && element.args().parmEnumType() == enumnum(RLedgerTurnoverSheetType))
        {
            qdbs = this.query().dataSourceTable(tableNum(VendTrans));

            qdbs_W = qdbs.addDataSource(tableNum(VendTrans_W), tableId2name(tableNum(VendTrans_W)));
            qdbs_W.addLink(fieldNum(VendTrans, RecId), fieldNum(VendTrans_W, VendTrans));
            qdbs_W.joinMode(JoinMode::OuterJoin);
        }
        //</GEERU>

        SysQuery::mergeRanges(this.query(), query);

        criteriaOpen = this.query().dataSourceNo(1).findRange(fieldNum(VendTrans,Closed));
    }

    if (criteriaOpen)
    {
        if (criteriaOpen.value() == strFmt('"\" %1..',dateNull()))
        {
            showOpen.value(true);
        }
    }
    else
    {
        criteriaOpen = this.query().dataSourceNo(1).addRange(fieldNum(VendTrans,Closed));
    }
    transTypeRange = SysQuery::findOrCreateRange(this.query().dataSourceNo(1), fieldnum(VendTrans, TransType));

    custVendTransDetails = new CustVendTransDetails(vendTrans);

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        this.cacheAddMethod(tableMethodStr(VendTrans, fiscalEstablishmentId_BR));
    }
    // </GBR>

    this.cacheAddMethod(tableMethodStr(VendTrans, originalInvoiceRegisterVoucher));

    // <GIN>
    // Added range to filter the Vendor transactions, for which the withhold taxes has been settled for authority.
    if (TaxWithholdParameters_IN::checkTaxParameters()
        && element.args().caller() != null
        && element.args().caller().name() == formStr(LedgerTaxWithholdJournal_IN))
    {
        //Filter the settled records.
        this.query().dataSourceTable(tableNum(VendTrans)).addDataSource(tableNum(TaxWithholdTrans_IN));
        this.query().dataSourceTable(tableNum(TaxWithholdTrans_IN)).addLink(fieldNum(VendTrans, Voucher), fieldNum(TaxWithholdTrans_IN, Voucher));
        this.query().dataSourceTable(tableNum(TaxWithholdTrans_IN)).addLink(fieldNum(VendTrans, TransDate), fieldNum(TaxWithholdTrans_IN, TransDate));
        this.query().dataSourceTable(tableNum(TaxWithholdTrans_IN)).addRange(fieldNum(TaxWithholdTrans_IN, SettledVoucher)).value(SysQuery::valueNotEmptyString());
        this.query().dataSourceTable(tableNum(TaxWithholdTrans_IN)).joinMode(JoinMode::NoExistsJoin);

        // Joining taxWithholdTrans_IN table again to make an Or condition to LastSettleVoucher field of VendTrans and Voucher field of TaxWithholdTrans_IN
        // This is to filter the payment transactions which are linked to invoice transactions which are settled.
        this.query().dataSourceTable(tableNum(VendTrans)).addDataSource(tableNum(TaxWithholdTrans_IN), #TaxWithholdTransTaxAdjustment);
        this.query().dataSourceName(#TaxWithholdTransTaxAdjustment).addLink(fieldNum(VendTrans, LastSettleVoucher), fieldNum(TaxWithholdTrans_IN, Voucher));
        this.query().dataSourceName(#TaxWithholdTransTaxAdjustment).addRange(fieldNum(TaxWithholdTrans_IN, SettledVoucher)).value(SysQuery::valueNotEmptyString());
        this.query().dataSourceName(#TaxWithholdTransTaxAdjustment).joinMode(JoinMode::NoExistsJoin);

        // Filter the tax adjustment records.
        this.query().dataSourceTable(tableNum(VendTrans)).addDataSource(tableNum(VendTrans_IN));
        this.query().dataSourceTable(tableNum(VendTrans_IN)).addLink(fieldNum(VendTrans, RecId), fieldNum(VendTrans_IN, VendTrans));
        this.query().dataSourceTable(tableNum(VendTrans_IN)).addRange(fieldNum(VendTrans_IN, VendTransRefRecId)).value(SysQuery::valueNot(0));
        this.query().dataSourceTable(tableNum(VendTrans_IN)).joinMode(JoinMode::NoExistsJoin);
    }
    // </GIN>

    element.initDRsCRsControls();
}

void executeQuery()
{
    // <GEEU>
    custVendCompensation.enabled(false);
    // </GEEU>
    super();
}

int active()
{
    int ret = super();
    // <GIN>
    #ISOCountryRegionCodes
    // </GIN>

    custVendTransDetails.setCustVendTrans(vendTrans);

    vendTrans_ds.allowEdit(false);
    vendTrans_W_ds.allowEdit(false);

    if (!vendTrans.open())
    {
        vendTrans_ds.allowEdit(false);
        vendTrans_W_ds.allowEdit(false);
        if (parentCoda)
        {
            include.allowEdit(false);
        }
    }
    else
    {
        vendTrans_ds.allowEdit(true);
        vendTrans_W_ds.allowEdit(true);
        if (parentCoda)
        {
            if (bankAccountStatementLines.isIncludedElsewhere(vendTrans.RecId, SysModule::Vend))
            {
                vendtrans_ds.allowEdit(false);
                vendTrans_W_ds.allowEdit(false);
                include.allowEdit(false);
            }
            else
            {
                include.allowEdit(true);
            }
        }
    }

    if (vendTrans.Invoice != '' || vendTrans.PromissoryNoteID != '')
    {
        vendPromissoryNoteInvoice.enabled(true);
        if (vendTrans.PromissoryNoteID != '')
        {
            vendPromissoryNoteInvoice.text("@SYS9954");
        }
        else
        {
            vendPromissoryNoteInvoice.text("@SYS71440");
        }
    }
    else
    {
        vendPromissoryNoteInvoice.enabled(false);
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        if (VendTransFormDisplayVendBankAccountFlight::instance().isEnabled())
        {
            if ((vendTrans.Invoice && (vendTrans.TransType == LedgerTransType::Purch || vendTrans.TransType == LedgerTransType::Vend || !vendTrans.TransType))
                    || vendTrans.TransType == LedgerTransType::Payment)
            {
                bank_ThirdPartyBankAccountId.enabled(true);
            }
            else
            {
                bank_ThirdPartyBankAccountId.enabled(false);
            }
        }
        else
        {
            vendTrans_ds.object(fieldNum(VendTrans,ThirdPartyBankAccountId)).visible(element.isVisibleVendBankAccountField());
        }
    }

    //<GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        vendReverseTransactions.enabled(VendTrans::checkReversal(vendTrans) && TaxWithholdTrans_IN::enableReverseButton(vendTrans));
        vendReverseTransBase.enabled((TransactionReversalTrans::findTransactionReversalTrans(tableNum(VendTrans), vendTrans.RecId) ? true : false) &&
                                      TaxWithholdTrans_IN::enableReverseButton(vendTrans));
    }
    else
    {
        //</GIN>
        vendReverseTransactions.enabled(VendTrans::checkReversal(vendTrans));
        vendReverseTransBase.enabled(TransactionReversalTrans::findTransactionReversalTrans(tableNum(VendTrans), vendTrans.RecId) ? true : false);
        //<GIN>
    }
    //</GIN>

    // <GEERU><GEEU>
    custVendPrepaymentHandling_W.enabled(!vendTrans.Invoice && !vendTrans.Closed);
    // </GEEU></GEERU>
    // <GEEU>
    advanceInvoiceLink_CZ.enabled(vendTrans.Prepayment && !vendTrans.Invoice && !vendTrans.Closed);
    // </GEEU>

    element.enableFieldsActive();

    // <GEEU>
    element.enableFieldsActive_W();
    // </GEEU>

    // <GEELV>
    if (showSettledTax)
    {
        showSettledTax.formLinkActive();
    }
    // </GEELV>

    // call to the method to set the Blocked Release Date and Release Comment according to the Approved field
    element.setStoppedReleaseFields();

    return ret;
}

