edit NoYes include(boolean set, VendTrans _vendTrans, NoYes _include)
{
    if (set)
    {
        if (_include == NoYes::Yes)
        {
            bankAccountStatementLines.includeInCoda(_vendTrans.RecId, SysModule::Vend);
        }
        else
        {
            bankAccountStatementLines.excludeFromCoda(_vendTrans.RecId, SysModule::Vend);
        }
    }
    else
    {
        _include = bankAccountStatementLines.isIncluded(_vendTrans.RecId, SysModule::Vend);
    }
    return _include;
}

public void write()
{
    super();

    //sets the release date comment blank if the release date is blank
    if (vendTrans.InvoiceReleaseDate == utcDateTimeNull())
    {
        vendTrans.ReleaseDateComment = "";
    }

    custVendTransDetails = new CustVendTransDetails(vendTrans);

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoGB]))
    {
        ttsBegin;
        VendTrans_W.SysExtensionSerializerExtensionMap::insertAfterBaseTable(vendTrans);
        ttsCommit;
    }
}

public void linkActive()
{
    BankChequePaymTrans paymTrans;
    TmpBankPaym2Invoice paymInvoice;
    TransDate transDate;
    Voucher voucher;
    CompanyId paymCompany;

    boolean setRange = false;

    Query q;
    QueryBuildDataSource qbdS;
    QueryBuildRange range;
    // <GEERU>
    SalesPurchBookDataMap_RU bookDataMap;
    CompanyId invCompany;
    RefRecId paymRecId = -1;
    RefRecId invRecId = -1;
    #isoCountryRegionCodes
    // </GEERU>

    switch (common.TableId)
    {
        case tableNum(BankChequePaymTrans):
            paymTrans = common;
            paymCompany = paymTrans.InvoiceCompany;
            transDate = paymTrans.InvoiceTransDate;
            voucher = paymTrans.InvoiceVoucher;
            setRange = true;
            break;
        case tableNum(TmpBankPaym2Invoice):
            paymInvoice = common;
            paymCompany = paymInvoice.InvoiceCompany;
            transDate = paymInvoice.InvoiceTransDate;
            voucher = paymInvoice.InvoiceVoucher;
            setRange = true;
            break;
        case tableNum(CFMPaymentRequestTable):
            CFMPaymentRequestTable paymentRequest = common;

            if (element.args().caller() is CFMPaymentRequestPaymentTrans)
            {
                CFMPaymentRequestPaymentTrans requestPaymentTrans = element.args().caller();
                paymRecId = requestPaymentTrans.findPaymentSource(tableNum(VendTrans));
            }
            else
            {
                paymRecId = paymentRequest.HeaderRefRecId;
            }

            setRange = true;
            break;

        case tableNum(CFMAnalyticFormDataEditTmp):
            CFMAnalyticFormDataEditTmp dataEditTmpTable = common;
            changecompany(dataEditTmpTable.SourceDataAreaId)
            {
                paymRecId = VendTransOpen::find(dataEditTmpTable.SourceRefRecId).RefRecId;
            }
            setRange =true;
            break;
        // <GEERU>
        case tableNum(SalesBookVATProcessLogTrans_RU):
        case tableNum(SalesBookTrans_RU):
        case tableNum(PurchBookVATProcessLogTrans_RU):
        case tableNum(PurchBookTrans_RU):
            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
            {
                bookDataMap = common;
                if (element.args().parmEnum() == NoYes::Yes)
                {
                    paymCompany = bookDataMap.PaymentCompany;
                    paymRecId = bookDataMap.PaymentRecIdRef;
                }
                else
                {
                    invCompany = bookDataMap.InvoiceCompany;
                    invRecId = bookDataMap.InvoiceRecIdRef;
                }
                setRange = true;
            }

            break;
        // </GEERU>
    }

    if (setRange)
    {
        q = vendTrans_ds.query();

        if (paymCompany != '')
        {
            q.clearCompanyRange();
            q.addCompanyRange(paymCompany);
        }

        qbdS = q.dataSourceTable(tableNum(VendTrans));

        // <GEERU>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
        {
            if (transDate)
            {
                range = qbdS.rangeField(fieldNum(VendTrans, TransDate));
                range.value(queryValue(transDate));
            }

            if (voucher)
            {
                range = qbdS.rangeField(fieldNum(VendTrans, Voucher));
                range.value(queryValue(voucher));
            }

            if (paymRecId != -1)
            {
                range = qbdS.rangeField(fieldNum(VendTrans, RecId));
                range.value(queryValue(paymRecId));
            }

            if (invRecId != -1)
            {
                range = qbdS.rangeField(fieldNum(VendTrans, RecId));
                range.value(queryValue(invRecId));
            }
        }
        else
        {
            // </GEERU>
            range = qbdS.rangeField(fieldNum(VendTrans, TransDate));
            range.value(queryValue(transDate));

            range = qbdS.rangeField(fieldNum(VendTrans, Voucher));
            range.value(queryValue(voucher));
            // <GEERU>
        }
        // </GEERU>
    }
    super();
}

/// <summary>
/// This method displays the transaction status in vendor transactions form
/// </summary>
/// <param name="_vendTrans">
/// Vendtrans buffer is passed as argument for which the transaction status should be displayed.
/// </param>
/// <returns>
/// Returns the Transaction status for the tranaction whether it is adjusted or posted.
/// </returns>
display TaxWithholdTransactionstatus_IN showtaxWithholdStatus_IN(VendTrans _vendTrans)
{
    TaxWithholdTrans_IN taxWithholdTrans;
    TaxWithholdTransactionstatus_IN transactionStatus = TaxWithholdTransactionstatus_IN::None;

    select firstonly TransactionStatus from taxWithholdTrans
        where taxWithholdTrans.SourceTransVoucher == _vendTrans.Voucher
           && taxWithholdTrans.SourceTransDate == _vendTrans.TransDate;

    if (taxWithholdTrans.RecId != 0)
    {
        transactionStatus = taxWithholdTrans.TransactionStatus;
    }
    else
    {
        select firstonly TransactionStatus from taxWithholdTrans
            where taxWithholdTrans.Voucher == _vendTrans.Voucher
               && taxWithholdTrans.TransDate == _vendTrans.TransDate;

        if (taxWithholdTrans.RecId != 0)
        {
            transactionStatus = taxWithholdTrans.TransactionStatus;
        }
    }

    return transactionStatus;
}

public void selectionChanged()
{
    super();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoHU, #isoPL]))
    {
        custVendCompensation.enabled(true);

        for (VendTrans trans = getFirstSelection(vendTrans_ds); trans; trans = vendTrans_ds.getNext())
        {
            if (trans.Closed)
            {
                custVendCompensation.enabled(false);
                break;
            }
        }
    }
}

public void markChanged()
{
    super();

    CustVendTransFilterHelper::setDefaultCompany(VendTrans.DataAreaId);

    if (isCrossCompanyView && VendTrans.company() != lastMarkedCompany)
    {
        element.setKnownCountryRegionFieldVisibility(VendTrans.company());

        lastMarkedCompany = VendTrans.company();
    }
}

public display VendName crossCompanyPartyName(VendTrans _vendTrans)
{
    DirPartyTable partyTable;
    VendTable vendTable;

    select firstonly crosscompany Name from partyTable
        exists join vendTable
            where partyTable.RecId == vendTable.Party
                && vendTable.AccountNum == _vendTrans.AccountNum
                && vendTable.DataAreaId == _vendTrans.DataAreaId;

    return partyTable.Name;
}

