public int active()
{
    int ret = super();

    // New records for the outer join data source VendTrans_W will have null values for all fields including DataAreaId.
    // For cross-company queries, a dataAreaId is not automatically enforced on new records.
    if (VendTrans_W.VendTrans == 0)
    {
        VendTrans_W_ds.company(VendTrans.company());
    }

    if (ret && TaxParameters::find().ServiceTax_IN)
    {
        vendTrans_W_ds.object(fieldNum(VendTrans_W, BlockTaxReversal_IN)).allowEdit(vendTrans_W.ServiceTaxPOTStatus_IN == ServiceTaxPOTStatus_IN::Original);
    }

    // <GJP>
    if (VendPaymFeeUtil_JP::isVendorPaymFeeEnabled())
    {
        PaymFee_JP.visible(VendTrans_W.PaymFeeTransactionCurrencyAmount_JP != 0);
    }
    // </GJP>

    return ret;
}

