void clicked()
{
    VendTable vendTable;
    CustInvoiceTable custInvoiceTable;
    Args args;
    FormRun formRun;
    DataAreaId tradingPartnerCompany;
    boolean hasMatchingInvoice = false;

    super();

    vendTable = VendTable::find(VendTrans.AccountNum);
    tradingPartnerCompany = vendTable.interCompanyTradingPartnerCompanyID();
    if (tradingPartnerCompany)
    {
        if (!TradeInterCompany::checkDataAreaAccess(tradingPartnerCompany))
        {
            return;
        }

        changeCompany (tradingPartnerCompany)
        {
            custInvoiceTable = CustInvoiceTable::findPostedInvoice(vendTable.interCompanyTradingPartnerAccount(),
                                                                   VendTrans.Invoice,
                                                                   VendTrans.TransDate);
            if (custInvoiceTable)
            {
                hasMatchingInvoice = true;
                args = new Args();
                args.record(custInvoiceTable);
                args.caller(this);
                args.name(formStr(ProjIntercompanyInvoice));
                args.menuItemType(MenuItemType::Display);
                args.menuItemName(MenuItemDisplayStr(ProjIntercompanyInvoice));

                formRun = classfactory.formRunClass(args);
                formRun.init();
                formRun.run();
                formRun.wait();
            }
        }
    }
    if (!hasMatchingInvoice)
    {
        // We display an error message instead of disabling the button to avoid additional queries that would
        // have a performance impact for all invoices.
        error("@SYS4001479");
    }
}

