public void jumpRef()
{
    VendTrans currentVendTrans = element.VendTrans;

    boolean hasMoreThanOneVendTransOpen = this.hasMoreThanOneVendTransOpen(currentVendTrans);

    if (hasMoreThanOneVendTransOpen)
    {
        this.openVendTransOpenDetailsForm(currentVendTrans);
    }
    else
    {
        this.openVendTransDueDateCashDiscountForm(currentVendTrans);
    }

    super();
}

private boolean hasMoreThanOneVendTransOpen(VendTrans _vendTrans)
{
    VendTransOpen vendTransOpen;

    select count(RecId) from vendTransOpen
            where vendTransOpen.RefRecId == _vendTrans.RecId;

    return vendTransOpen.RecId > 1;
}

private void openVendTransDueDateCashDiscountForm(VendTrans _vendTrans)
{
    changecompany(_vendTrans.DataAreaId)
    {
        MenuFunction menuFunction = new MenuFunction(menuItemDisplayStr(VendTransDueDateCashDiscount), MenuItemType::Display);

        if (menuFunction && menuFunction.checkAccessRights())
        {
            Args args = new Args();
            args.record(_vendTrans);
            args.caller(element);

            menuFunction.run(args);
        }
        else
        {
            this.openVendTransOpenDetailsForm(_vendTrans);
        }
    }
}

private void openVendTransOpenDetailsForm(VendTrans _vendTrans)
{
    changecompany(_vendTrans.DataAreaId)
    {
        MenuFunction menuFunction = new MenuFunction(menuItemDisplayStr(VendTransOpen), MenuItemType::Display);

        if (menuFunction && menuFunction.checkAccessRights())
        {
            Args args = new Args();
            args.record(_vendTrans);
            Args.caller(element);

            menuFunction.run(args);
        }
    }
}

