public void clicked()
{
    if (accountType.selection() != LedgerJournalACType::Ledger)
    {
        accountDimension.enabled(true);
    }
    else
    {
        accountDimension.enabled(false);
    }

    if (offsetAccountType.selection() != LedgerJournalACType::Ledger && offsetAccount.value() != 0)
    {
        offsetAccountDimension.enabled(true);
    }
    else
    {
        offsetAccountDimension.enabled(false);
    }

    super();
}

public void clicked()
{
    ledgerJournalEngine.openAccountFinancialDimensionForm(ledgerJournalTrans, accountDimension.menuItemType(), accountDimension.menuItemName());
}

public void clicked()
{
    ledgerJournalEngine.openOffsetAccountFinancialDimensionForm(ledgerJournalTrans, offsetAccountDimension.menuItemType(), offsetAccountDimension.menuItemName());
}

