[Form]
public class VendUnpostedInvoiceJournal extends FormRun
{
    InventTrans inventTrans;
    VendInvoiceId unpostedInvoiceId;
    InventDimCtrl_Frm_Mov inventDimFormSetup;
    int vendUnpostedInvoiceInquiryType;
    CustVendInvoiceAttachments custVendInvoiceAttachments;

    #define.PurchTable(0)
    #define.VendTable(1)
    #define.InventTrans(2)
    #define.VendPackingSlipTrans(3)
    #define.VendInvoiceInfoTable(4)

    boolean useInvoiceMatching;
    private boolean isIncludedInProcessAutomation;

}

/// <summary>
/// Called when the window is first opened.
/// </summary>
/// <remarks>
/// Preps the datasources by copying all unique rows from the VendInvoiceInfoTable and PurchParmTable tables
/// into the VendTmpInvoiceInfoTable and the the VendInvoiceInfoLine and PurchParmLine tables into
/// the VendTmpInvoiceInfoLine tables.
/// </remarks>
public void init()
{
    super();

    if (element.args().record())
    {
        switch (element.args().record().TableId)
        {
            case tableNum(PurchTable) :
                vendUnpostedInvoiceInquiryType = #PurchTable;
                break;

            case tableNum(VendTable) :
                vendUnpostedInvoiceInquiryType = #VendTable;
                break;

            case tableNum(InventTrans) :
                vendUnpostedInvoiceInquiryType = #InventTrans;
                break;

            case tableNum(VendPackingSlipTrans) :
                vendUnpostedInvoiceInquiryType = #VendPackingSlipTrans;
                break;

            case tableNum(VendInvoiceInfoTable) :
                vendUnpostedInvoiceInquiryType = #VendInvoiceInfoTable;
                isIncludedInProcessAutomation = VendInvoiceAutomationStateManagement::isIncludedInAutomatedProcessing(this.args().record());
                break;
        }
    }

    if ((vendUnpostedInvoiceInquiryType == #InventTrans) || (vendUnpostedInvoiceInquiryType == #VendPackingSlipTrans))
    {
        // Update the window title to show information relating to the inquiry type
        element.design().caption("@SYS113503");
    }

    useInvoiceMatching = VendParameters::find(false).editUseInvoiceMatching(false) == NoYes::Yes;
    element.enableMatchingDetailsButton(!isIncludedInProcessAutomation);

    element.updateDesign(InventDimFormDesignUpdate::Init);

    //Make VendPaymentGroup visible if the AP Payment group feature is enabled
    vendTmpInvoiceInfoTable_VendPaymentGroup.visible(VendParameters::find().VendPaymentGroupingEnable == NoYes::Yes);

    custVendInvoiceAttachments = CustVendInvoiceAttachments::construct();

    element.hideSystemDefinedAttachmentsButton();

    //set visibility on invoice grouping fields
    invoiceGrouping.visible(VendParameters::find().VendInvoiceGroupingEnable == NoYes::Yes);
}

private void initData()
{
    delete_from vendTmpInvoiceInfoTable;
    delete_from vendTmpInvoiceInfoLine;

    if (element.args().record())
    {
        switch (element.args().record().TableId)
        {
            case tableNum(PurchTable) :
                vendTmpInvoiceInfoTable.initFromPurchTable(this.args().record());
                vendTmpInvoiceInfoLine.initFromPurchTable(this.args().record());
                break;

            case tableNum(VendTable) :
                vendTmpInvoiceInfoTable.initFromVendTable(this.args().record());
                vendTmpInvoiceInfoLine.initFromVendTable(this.args().record());
                break;

            case tableNum(InventTrans) :
                vendTmpInvoiceInfoTable.initFromInventTrans(this.args().record());
                vendTmpInvoiceInfoLine.initFromInventTrans(this.args().record());
                break;

            case tableNum(VendPackingSlipTrans) :
                vendTmpInvoiceInfoTable.initFromVendPackingSlipTrans(this.args().record());
                vendTmpInvoiceInfoLine.initFromVendPackingSlipTrans(this.args().record());
                break;

            case tableNum(VendInvoiceInfoTable) :
                vendTmpInvoiceInfoTable.initFromVendInvoiceInfoTable(this.args().record());
                vendTmpInvoiceInfoLine.initFromVendInvoiceInfoTable(this.args().record());
                break;
        }
    }
}

