Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

public int task(int _taskId)
{
    #task
    int ret;

    switch (_taskId)
    {
        case #taskPrint :
        case #taskPrintPreview :
            ret = 0;
            break;

        default :
            ret = super(_taskId);
    }

    return ret;
}

/// <summary>
/// This method is used in connection with showing the inventory dimensions.
/// </summary>
/// <param name="_mode">
/// Should either be InventDimFormDesignUpdate::Init or InventDimFormDesignUpdate::Active.
/// </param>
/// <exception cref="Exception::Error">
/// The parameter expected should either be InventDimFormDesignUpdate::Init or InventDimFormDesignUpdate::Active
/// </exception>
void updateDesign(InventDimFormDesignUpdate _mode)
{
    PurchLine purchLine;

    InventMovement movement = InventMovement::construct(purchLine);

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default :
            throw error(strFmt("@SYS54195", funcName()));
    }
}

/// <summary>
/// Hides the system defined attachments button.
/// </summary>
protected void hideSystemDefinedAttachmentsButton()
{
    #SysSystemDefinedButtons

    FormCommandButtonControl attachButton = this.control(this.controlId(#SystemDefinedAttachButton)) as FormCommandButtonControl;

    if (attachButton)
    {
        attachButton.visible(false);
    }
}

/// <summary>
/// Sets the label on the header attachment button.
/// </summary>
/// <param name = "_fromCache">true if the attachment count should come from the cache; otherwise, false.</param>
protected void setHeaderAttachmentButtonLabel(boolean _fromCache = true)
{
    HeaderAttachments.text(custVendInvoiceAttachments.getAttachmentCountButtonLabel(vendInvoiceInfoTable, "@SYS114630", _fromCache));
}

/// <summary>
/// Displays the attachments for the given table buffer.
/// </summary>
/// <param name = "_record">The table buffer for which documents are to be attached.</param>
public void showDocuments(Common _record)
{
    if (_record)
    {
        Args args = new Args(menuItemDisplayStr(DocuAttachment));

        args.caller(element);
        args.record(_record);
        args.menuItemName(menuitemDisplayStr(DocuAttachment));
        args.menuItemType(MenuItemType::Display);

        FormRun formRun = classfactory.formRunClass(args);
        formRun.init();
        formRun.run();
        formRun.wait();
    }
}

public int active()
{
    str windowCaption;
    int ret = super();

    HeaderAttachments.visible(true);
    element.setHeaderAttachmentButtonLabel();

    if ((vendUnpostedInvoiceInquiryType == #PurchTable) || (vendUnpostedInvoiceInquiryType == #VendTable) ||
        (vendUnpostedInvoiceInquiryType == #VendInvoiceInfoTable))
    {
        //Pending invoices - Invoice: %1, %2
        windowCaption = "@SYS112150";
    }
    else if ((vendUnpostedInvoiceInquiryType == #InventTrans) || (vendUnpostedInvoiceInquiryType == #VendPackingSlipTrans))
    {
        //Unposted matched invoices - Invoice: %1, %2
        windowCaption = "@SYS112151";
    }

    // Update the window title to show information relating to the active row
    element.design().caption(strFmt(windowCaption, vendTmpInvoiceInfoTable.Num, vendTmpInvoiceInfoTable.InvoiceAccount));

    return ret;
}

public void init()
{
    super();

    // Clear parent form's restriction.
    this.query().dataSourceTable(tableNum(VendTmpInvoiceInfoTable)).clearDynalinks();
}

void clicked()
{
    //Reread the current record in case another user has modified the invoice after this form has been opened and last read from the database
    vendTmpInvoiceInfoLine_DS.research(true);

    if (vendTmpInvoiceInfoTable.Ordering == DocumentStatus::ApproveJournal)
    {
        buttonEditInvoiceFromPool.clicked();
    }
    else
    {
        super();
    }

    VendTmpInvoiceInfoTable_ds.executeQuery();
}
