Method.Source:
    [Form]
    public class VendUnrealizedRev extends FormRun
    {
        boolean     researchData;
}

/// <summary>
///     Refreshes <c>VendUnrealizedRev</c> form when this form is closed.
/// </summary>
/// <param name="_research">
///     Indicates whether the data source of <c>VendUnrealizedRev</c> should be researched.
/// </param>
public void bookRefresh(boolean _research = false)
{
    FormRun         callerForm = element.args().caller();
    FormDataSource  vendUnrealizedRevListPageDS;

    if (callerForm)
    {
        vendUnrealizedRevListPageDS = callerForm.dataSource();
        if (_research)
        {
            vendUnrealizedRevListPageDS.research();
        }
        else
        {
            vendUnrealizedRevListPageDS.reread();
            vendUnrealizedRevListPageDS.refresh();
        }

        // Call linkActive to active data source and update form design.
        vendUnrealizedRevListPageDS.executeQuery();
    }
}

    public void close()
{
    this.bookRefresh(researchData);
    super();
}

    public void init()
{
    if (!TaxThaiGovCertificationFeatureChecker::isUnrealizedVATEnabled())
    {
        // The 'Use tax branch' or 'Manage realized and unrealized VAT' parameter is not selected.
        throw error("@SYS4082912");
    }

    if (!element.args().record())
    {
        // Form %1 requires an active buffer.
        throw error(strFmt("@SYS22678", element.name()));
    }

    super();
}

    public void markAll(boolean _markAll = true)
{
    VendSettlementExtensionTH   vendSettlementExtensionTHLocal;

    ttsbegin;
    vendSettlementExtensionTH_ds.first();

    do
    {
        vendSettlementExtensionTHLocal = vendSettlementExtensionTH_ds.cursor();
        if (vendSettlementExtensionTHLocal)
        {
            CustVendUnrealizedRevType::markTrans(vendSettlementExtensionTHLocal, vendUnrealizedRev, _markAll);
            vendSettlementExtensionTH_ds.reread();
            vendSettlementExtensionTH_ds.refresh();
        }
    }
    while (vendSettlementExtensionTH_ds.next());

    ttscommit;
}

    public void setTitleText()
{
    headerTitle.text(strFmt('%1 : %2 - %3',
        vendUnrealizedRev.ReversalId,
        vendUnrealizedRev.VendTable,
        vendUnrealizedRev.name()));
}

    public void updateDesign()
{
    if (vendUnrealizedRev.Posted)
    {
        checkBox.allowEdit(!vendUnrealizedRev.Posted);

        vendUnrealizedRev_ds.object(fieldNum(VendUnrealizedRev, TaxInvoiceId)).allowEdit(!vendUnrealizedRev.Posted);
        vendUnrealizedRev_ds.object(fieldNum(VendUnrealizedRev, TaxInvoiceDate)).allowEdit(!vendUnrealizedRev.Posted);
        vendUnrealizedRev_ds.object(fieldNum(VendUnrealizedRev, TaxInvoiceReceiptDate)).allowEdit(!vendUnrealizedRev.Posted);
        vendUnrealizedRev_ds.object(fieldNum(VendUnrealizedRev, RealVendName)).allowEdit(!vendUnrealizedRev.Posted);
    }
}

    public void updateUnrealizedRev()
{
    Query                       query;
    QueryRun                    queryRun;
    VendUnrealizedRevType       unrealizedRevType;

    unrealizedRevType = VendUnrealizedRevType::construct(vendUnrealizedRev);
    query = unrealizedRevType.buildUpdateQuery();

    queryRun = new QueryRun(query);
    if (queryRun.prompt())
    {
        VendUnrealizedRevType::update(vendUnrealizedRev.RecId, queryRun.query());
        vendUnrealizedRev_ds.reread();
        vendUnrealizedRev_ds.refresh();
        vendSettlementExtensionTH_ds.executeQuery();
    }
}

/// <summary>
/// Retrieves the settled amount plus withholding tax amount.
/// </summary>
/// <returns>The settled amount plus withholding tax amount.</returns>
public display AmountMST displaySettledAmountWithholdingTax()
{
    TaxWithholdTrans taxWithholdTrans;
    VendSettlement vendSettlementOffset;

    select firstonly sum(PaymTaxWithholdAmount) from taxWithholdTrans
        where taxWithholdTrans.VendTransInvoiceId == this.VendSettlement.OffsetRecid
        join RecId from vendSettlementOffset
            where VendSettlementOffset.OffsetRecid == this.VendSettlement.TransRecId
                && vendSettlementOffset.TransRecId == this.VendSettlement.OffsetRecId
                && vendSettlementOffset.OffsetTransVoucher == taxWithholdTrans.Voucher;

    return this.VendSettlement.SettleAmountCur + TaxWithholdTrans.PaymTaxWithholdAmount;
}

        public void delete()
{
    super();

    researchData = true;
    element.close();
}

        public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        element.updateDesign();
    }

    element.setTitleText();
    return ret;
}

        public void init()
{
    QueryBuildRange qbr;

    super();

    qbr = this.query().dataSourceTable(tableNum(VendSettlement)).addRange(fieldNum(VendSettlement, CanBeReversed));
    qbr.value(queryValue(NoYes::Yes));
}

        public void init()
{
    QueryBuildDataSource    qbds;

    super();

    qbds = this.query().dataSourceName(vendTransInvoice_ds.name());
    qbds.clearLinks();
    qbds.addLink(fieldNum(VendSettlement, OffsetRecid), fieldNum(VendTrans, RecId));
}

        public display TaxAmountCur amountCurToReverse(TaxTrans _taxTrans)
{
    return _taxTrans.getAmountToReverse(vendSettlement);
}

        public void init()
{
    QueryBuildDataSource    qbds;
    QueryBuildRange         qbr;

    super();

    qbds = this.query().dataSourceTable(tableNum(TaxTrans));
    qbds.clearDynalinks();
    qbds.addDynalink(fieldNum(TaxTrans, Voucher), vendTransInvoice, fieldNum(VendTrans, Voucher));
    qbds.addDynalink(fieldNum(TaxTrans, TransDate), vendTransInvoice, fieldNum(VendTrans, TransDate));

    qbds = qbds.addDataSource(tableNum(TaxTransExtensionTH));
    qbds.joinMode(JoinMode::InnerJoin);
    qbds.addLink(fieldNum(TaxTrans, RecId), fieldNum(TaxTransExtensionTH, TaxTrans));
    qbr = qbds.addRange(fieldNum(TaxTransExtensionTH, TaxInvoiceId));
    qbr.value(SysQuery::valueEmptyString());
}

public display TaxAmountCur amountOriginToReverse(TaxTrans _taxTrans)
{
    return _taxTrans.getAmountOriginToReverse(vendSettlement);
}

        public edit NoYes editMarkTrans(
    boolean                     _set,
    VendSettlementExtensionTH   _vendSettlementExtensionTH,
    NoYes                       _markTrans)
{
    if (_set)
    {
        ttsbegin;
        CustVendUnrealizedRevType::markTrans(_vendSettlementExtensionTH, vendUnrealizedRev, _markTrans);
        ttscommit;

        vendSettlementExtensionTH_ds.reread();
        vendSettlementExtensionTH_ds.refresh();
    }

    return (_vendSettlementExtensionTH.VendUnrealizedRev != 0);
}

        void clicked()
{
    super();

    element.updateUnrealizedRev();
}

        void clicked()
{
    super();

    element.markAll();
}

        void clicked()
{
    super();

    element.markAll(false);
}
