/// <summary>
/// Register the data source to which the filter should be applied.
/// </summary>
public void registerDatasourceOnQueryingEvent()
{
    DirPerson_ds.OnQueryExecuting += eventhandler(this.parmFilterFormQueryEventHandler().applyFilter);
}

/// <summary>
/// Show the newly created record in details view
/// </summary>
/// <param name="_dirPerson">
/// The record created on the dialog
/// </param>
public void showCreatedRecordInDetailsView(DirPerson _dirPerson)
{
    DirPerson_DS.research();
    DirPerson_DS.queryBuildDataSource().clearDynalinks();
    DirPerson_DS.executeQuery();
    DirPerson_DS.positionToRecord(_dirPerson);
    element.viewOptionHelper().setViewOption(FormViewOption::Details);
}

private void updateHeaderControls()
{
    boolean isVendExtAdmin = (DirPerson.RecId == DirPersonUser::find(curUserId()).PersonParty);

    DeletePerson.enabled(!isVendExtAdmin);
    CreateContactPerson.enabled(!isVendExtAdmin);
    LineStripDelete.enabled(!isVendExtAdmin);
    this.enableVendorPortalUserRequestControls(isVendExtAdmin);
}

private void updateContactForLineToolBarControls(boolean _isVendExtAdmin = DirPerson.RecId == DirPersonUser::find(curUserId()).PersonParty)
{
    this.enableContactActivationInactivationControls(_isVendExtAdmin);
    this.enableAllowRemoveVendorPortalAccessControls(_isVendExtAdmin);
}

private void enableVendorPortalUserRequestControls(boolean _isVendExtAdmin)
{
    boolean activeUser = userinfo.id != "" && userinfo.enable;
    boolean pendingVendorPortalUserRequest = VendVendorPortalUserRequest::existsPendingRequest(DirPerson.RecId);

    VendVendorPortalProvisionUser.enabled(!_isVendExtAdmin && !activeUser && !pendingVendorPortalUserRequest);
    VendVendorPortalInactivateUser.enabled(!_isVendExtAdmin && activeUser && !pendingVendorPortalUserRequest);
    VendVendorPortalMaintainUserRoles.enabled(!_isVendExtAdmin && activeUser && !pendingVendorPortalUserRequest);
}

private void enableAllowRemoveVendorPortalAccessControls(boolean _isVendExtAdmin)
{
    VendVendorPortalAllowAccess.enabled(!_isVendExtAdmin && !ContactPerson.VendorPortalAccessAllowed && !ContactPerson.inActive);
    VendVendorPortalRemoveAccess.enabled(!_isVendExtAdmin && ContactPerson.VendorPortalAccessAllowed);
}

private void enableContactActivationInactivationControls(boolean _isVendExtAdmin)
{
    Inactivate.enabled(!_isVendExtAdmin && !ContactPerson.inActive);
    Activate.enabled(!_isVendExtAdmin && ContactPerson.inActive);
}

/// <summary>
/// If this form is called from <c>VendVendorProfileManagementWorkspace</c>,
/// then it should show only for the current company.
/// </summary>
public void init()
{
    VendVendorPortalUserRequestManager::validateVendorPortalUserAccessToAtLeastOneVendor(this);

    super();

    if (element.args() && element.args().caller() && element.args().caller().name() == formStr(VendVendorProfileManagementWorkspace))
    {
        DirPerson_DS.query().allowCrossCompany(false);
    }
}

public display LogisticsElectronicAddressLocator displayPrimaryEmail(DirPerson _dirPerson)
{
    return LogisticsElectronicAddress::findRecId(_dirPerson.PrimaryContactEmail).Locator;
}

public void delete()
{
    boolean deleteAllRelatedContactPerson()
    {
        boolean ret = !ContactPerson::findByPartyRecId(DirPerson_ds.cursor().RecId)
            .partyOfVendorContactPersonHasVendorContactRelationToOtherVendors();

        if (ret)
        {
            Query query = new Query();
            query.allowCrossCompany(true);
            QueryBuildDataSource qbdsContactPerson = query.addDataSource(tablenum(ContactPerson));
            qbdsContactPerson.addRange(fieldNum(ContactPerson, Party))
                .value(SysQueryRangeUtil::value(DirPerson_ds.cursor().RecId));

            QueryRun qbdsContactPersonRun = new QueryRun(query);

            while (qbdsContactPersonRun.next())
            {
                ret = qbdsContactPersonRun.get(tableNum(ContactPerson)).deleteVendorContactPerson() && ret;
            }
        }

        return ret;
    }

    ttsbegin;

    if (DirPersonUser::findParty(DirPerson_ds.cursor().RecId).RecId == 0
         && deleteAllRelatedContactPerson())
    {
        super();
    }
    else
    {
        throw error ("@SCM:NotAllowedToDeleteTheContactPerson");
    }

    ttscommit;
}

public int active()
{
    int ret;

    ret = super();

    element.updateHeaderControls();

    return ret;
}

public boolean validateDelete()
{
    boolean ret = ContactPerson.checkVendorContactPersonCanBeDeletedByUser();

    if (ret &&  DirPersonUser.RecId != 0)
    {
        ret = checkFailed("@SCM:NotAllowedToDeleteTheContactPerson");
    }
    else if (ret && ContactPerson.getCountOfContactsForParty() == 1
        && !ContactPerson.partyOfVendorContactPersonHasVendorContactRelationToOtherVendors())
    {
        ret = checkFailed(strFmt(("@SCM:VendorPortalOnlyOneContactPersonRelationExists"), VendTable::findByPartyRecId(ContactPerson.ContactForParty).name(), ContactPerson.displayCompanyName()));
    }

    ret = ret && super();

    return ret;
}

public int active()
{
    int ret;

    ret = super();

    element.updateContactForLineToolBarControls();

    return ret;
}

