public void closeOk()
{
    super();

    VendCurrentVendorResultTmp selectedVendCurrentVendorResultTmp = getFirstSelection(VendCurrentVendorResultTmp_ds);
    List vendAccountNumList = new List(Types::String);

    if (parentObject != null
        && parentObject.name() == formStr(VendUnsolicitedVendor))
    {
        parentObject.addVendorFromVendorSearch(selectedVendCurrentVendorResultTmp.VendorDataAreaId, selectedVendCurrentVendorResultTmp.VendorAccountNumber);
    }
    else
    {
        while (selectedVendCurrentVendorResultTmp)
        {
            vendAccountNumList.addEnd(selectedVendCurrentVendorResultTmp.VendorAccountNumber);
            selectedVendCurrentVendorResultTmp = VendCurrentVendorResultTmp_ds.getNext();
        }

        if (parentObject != null && (parentObject.name() == formStr(PurchRFQCaseTable)))
        {
            parentObject.addVendorsFromVendorSearch(vendAccountNumList);
        }
    }
}

public void deleteSelectedRecord(FormDataSource _vendorTypeDS)
{
    if (_vendorTypeDS.anyMarked())
    {
        _vendorTypeDS.deleteMarked();
    }
    else
    {
        _vendorTypeDS.delete();
    }
    _vendorTypeDS.research();
}

/// <summary>
/// Executes the return queries for each data sources
/// </summary>
public void executeQuery()
{
    this.executeCurrentVendorQuery();
    this.executeProspectiveVendorQuery();
    this.executeUnsolicitedVendorQuery();
}

private void executeCurrentVendorQuery()
{
    //Current Vendor Grid Refill Section
    currentVendorsTab.visible(false);

    if (queryCurrentVendor != null)
    {
        if (isAllLegalEntitiesIncluded)
        {
            DataArea dataArea;
            while select id from dataArea
            {
                changecompany(dataArea.id)
                {
                    this.insertCurrentVendor();
                }
            }
        }
        else
        {
            if (dataAreaId != '' && dataAreaId != curext())
            {
                changecompany(dataAreaId)
                {
                    this.insertCurrentVendor();
                }
            }
            else
            {
                this.insertCurrentVendor();
            }
        }

        vendCurrentVendorResultTmp_ds.research();

        currentVendorsTab.visible(true);
        this.selectControl(gridCurrentVendors);

        boolean enableVendorActionPane = vendCurrentVendorResultTmp_ds.getFirst() != null;
        currentVendorActionPane.enabled(enableVendorActionPane);
    }
}

private void insertCurrentVendor()
{
    Query queryCurrentVendorInsert = new Query(queryCurrentVendor);
    queryCurrentVendorInsert.clearAllFields();
    Map targetToSourceMapCurrentVendor = this.buildVendCurrentVendorResultTmpMap(queryCurrentVendorInsert);
    Query::insert_recordset(currentVendor, targetToSourceMapCurrentVendor, queryCurrentVendorInsert);
}

protected Map buildVendCurrentVendorResultTmpMap(Query _vendorQuery)
{
    Map targetToSourceMap = new Map(Types::String, Types::Container);

    QueryBuildDataSource qbdsVendTable = _vendorQuery.dataSourceTable(tableNum(VendTable));
    qbdsVendTable.fields().addField(fieldNum(VendTable, AccountNum));
    qbdsVendTable.fields().addField(fieldNum(VendTable, Blocked));
    qbdsVendTable.fields().addField(fieldNum(VendTable, DataAreaId));
    qbdsVendTable.fields().addField(fieldNum(VendTable, RecId));

    QueryBuildDataSource qbdsDirPartyTable = _vendorQuery.dataSourceTable(tableNum(DirPartyTable));
    qbdsDirPartyTable.fields().addField(fieldNum(DirPartyTable, Name));
    qbdsDirPartyTable.fields().addField(fieldNum(DirPartyTable, NameAlias));

    targetToSourceMap.insert(fieldStr(VendCurrentVendorResultTmp, VendorAccountNumber), [qbdsVendTable.uniqueId(), fieldStr(VendTable, AccountNum)]);
    targetToSourceMap.insert(fieldStr(VendCurrentVendorResultTmp, Blocked), [qbdsVendTable.uniqueId(), fieldStr(VendTable, Blocked)]);
    targetToSourceMap.insert(fieldStr(VendCurrentVendorResultTmp, VendorDataAreaId), [qbdsVendTable.uniqueId(), fieldStr(VendTable, DataAreaId)]);
    targetToSourceMap.insert(fieldStr(VendCurrentVendorResultTmp, VendTableRecId), [qbdsVendTable.uniqueId(), fieldStr(VendTable, RecId)]);
    targetToSourceMap.insert(fieldStr(VendCurrentVendorResultTmp, Name), [qbdsDirPartyTable.uniqueId(), fieldStr(DirPartyTable, Name)]);
    targetToSourceMap.insert(fieldStr(VendCurrentVendorResultTmp, SearchName), [qbdsDirPartyTable.uniqueId(), fieldStr(DirPartyTable, NameAlias)]);

    return targetToSourceMap;
}

private void executeProspectiveVendorQuery()
{
    QueryRun                            qrProspective;
    VendRequest                         vendRequest;
    DirPartyTable                       dirPartyTable;
    LogisticsPostalAddress              logisticsPostalAddress;
    int                                 prospectiveVendorCount;

    //Prospective Vendor Grid Refill Section
    prospectiveVendorsTab.visible(false);

    if (queryProspectiveVendor != null
        && isConfigurationkeyEnabled(configurationKeyNum(VendRequest)))
    {
        if (isAllLegalEntitiesIncluded)
        {
            queryProspectiveVendor.allowCrossCompany(true);
        }

        qrProspective = new QueryRun(queryProspectiveVendor);
        prospectiveVendorCount = 0;
        while (qrProspective.next())
        {
            vendRequest = qrProspective.get(tableNum(VendRequest));
            dirPartyTable = qrProspective.get(tableNum(DirPartyTable));
            logisticsPostalAddress = qrProspective.get(tableNum(LogisticsPostalAddress));

            prospectiveVendor.clear();
            prospectiveVendor.VendorAccountNumber = vendRequest.AccountNum;
            prospectiveVendor.Name = dirPartyTable.Name;
            prospectiveVendor.SearchName = dirPartyTable.NameAlias;
            prospectiveVendor.City = logisticsPostalAddress.City;
            prospectiveVendor.CountryRegionId = logisticsPostalAddress.CountryRegionId;
            prospectiveVendor.VendorRequestDataAreaId = vendRequest.DataAreaId;
            prospectiveVendor.VendorRequestNumber = vendRequest.VendRequestNumber;
            prospectiveVendor.VendorRequestRecId = vendRequest.RecId;
            prospectiveVendor.insert();
            prospectiveVendorCount++;
        }

        vendProspectiveVendorResultTmp_ds.research();
        prospectiveVendorsTab.visible(true);
        prospectiveVendorActionPane.enabled(prospectiveVendorCount > 0);

        if (this.selectedControl())
        {
            this.selectControl(gridProspectiveVendors);
        }
    }
}

private void executeUnsolicitedVendorQuery()
{
    QueryRun                            qrUnsolicited;
    VendRequestSignup                   vendRequestSignup;
    int                                 unsolicitedVendorCount;

    //Unsolicited Vendor Grid Refill Section
    unsolicitedVendorsTab.visible(false);

    if (queryUnsolicitedVendor != null
        && isConfigurationkeyEnabled(configurationKeyNum(VendUnsolicitedRegistration)))
    {
        queryUnsolicitedVendor.allowCrossCompany(true);
        qrUnsolicited = new QueryRun(queryUnsolicitedVendor);

        unsolicitedVendorCount = 0;
        while (qrUnsolicited.next())
        {
            vendRequestSignup = qrUnsolicited.get(tableNum(VendRequestSignup));

            unsolicitedVendor.clear();
            unsolicitedVendor.Name = vendRequestSignup.Name;
            unsolicitedVendor.SearchName = vendRequestSignup.Name;
            unsolicitedVendor.City = vendRequestSignup.City;
            unsolicitedVendor.CountryRegionId = vendRequestSignup.CountryRegionId;
            unsolicitedVendor.RequestSignUp = vendRequestSignup.RecId;
            unsolicitedVendor.insert();
            unsolicitedVendorCount++;
        }

        vendUnsolicitedVendorResultTmp_ds.research();
        unsolicitedVendorsTab.visible(true);
        unsolicitedVendorActionPane.enabled(unsolicitedVendorCount > 0 ? true : false);

        if (this.selectedControl())
        {
            this.selectControl(gridUnsolicitedVendors);
        }
    }
}

