public Object parmParentObject(Object _parentObject = parentObject)
{
    parentObject = _parentObject;

    if (parentObject)
    {
        switch (parentObject.name())
        {
            case formStr(PurchRFQCaseTable):
                okCancel.visible(true);
                showCurrentVendorOnly = true;
                prospectiveVendorsTab.visible(false);
                unsolicitedVendorsTab.visible(false);
                currentVendorFunctions.visible(false);
                currentVendorInquiries.visible(false);
                dialogCopyVendor.visible(false);
                break;
        }
    }

    return parentObject;
}

