private boolean isCurrentVendorBlockedForRequisition()
{
    VendCurrentVendorResultTmp selectedVendCurrentVendorResultTmp = getFirstSelection(vendCurrentVendorResultTmp_ds);
    if (selectedVendCurrentVendorResultTmp.Blocked == CustVendorBlocked::All ||
        selectedVendCurrentVendorResultTmp.Blocked == CustVendorBlocked::Requisition)
    {
        return true;
    }

    return false;
}

public void init()
{
    super();
    vendTableForTransaction_ds.init();
}

private boolean isSelectedVendorFromCurrentCompany()
{
    VendCurrentVendorResultTmp selectedVendCurrentVendorResultTmp = getFirstSelection(VendCurrentVendorResultTmp_ds);
    DataAreaId currentVendorDataAreaId = selectedVendCurrentVendorResultTmp.VendorDataAreaId;

    return (currentVendorDataAreaId == CompanyInfo::find().DataArea);
}

public int active()
{
    int ret = super();

    boolean isOnHold = this.isCurrentVendorBlockedForRequisition();
    boolean isMultipleLeSelected = this.isCurrentVendorFromSameLE();
    requestForQuote.enabled(isMultipleLeSelected);

    boolean isAllSelectedLECurrentLE = this.isSelectedVendorFromCurrentCompany();
    if (isMultipleLeSelected)
    {
        requestForQuote.enabled(isAllSelectedLECurrentLE && !isOnHold);
    }
    purchaseOrder.enabled(isAllSelectedLECurrentLE);
    dialogCopyVendor.enabled(!isAllSelectedLECurrentLE);

    return ret;
}

private boolean isCurrentVendorFromSameLE()
{
    VendCurrentVendorResultTmp selectedVendCurrentVendorResultTmp = getFirstSelection(vendCurrentVendorResultTmp_ds);
    DataAreaId currentVendorDataAreaId = selectedVendCurrentVendorResultTmp.VendorDataAreaId;
    while (selectedVendCurrentVendorResultTmp)
    {
        if (currentVendorDataAreaId != selectedVendCurrentVendorResultTmp.VendorDataAreaId)
        {
            return false;
        }
        selectedVendCurrentVendorResultTmp = vendCurrentVendorResultTmp_ds.getNext();
    }

    return true;
}

