{
    vendTableForTransaction_ds.query().dataSourceTable(tableNum(VendTable)).findRange(fieldNum(VendTable, RecId)).value(queryValue(vendCurrentVendorResultTmp.VendTableRecId));
    vendTableForTransaction_ds.executeQuery();

    super();
}

