[Form]
public class VendorPaymentHistory extends FormRun
{
    private const Voucher NullVoucher = '-';

    private QueryBuildRange rangeLedgerEntryJournal;
    private QueryBuildRange ledgerJournalTransRangeTransDate;
    private QueryBuildRange ledgerJournalTransRangeVoucher;

    private Voucher voucher;
    private LedgerJournalId journalNumber;
    private AccountNum vendAccount;

    private TransDate transDate;
    private VendTransRefRecId vendTransRecid, lastVendTransRecid;

}

/// <summary>
/// Gets the Amount of the Vendor Payment.
/// </summary>
/// <returns>
/// The <c>AmountMST</c> field.
/// </returns>
/// <remarks>
/// The method returns the amount for Vendor Payment using <c>ledgerJournalTrans</c> table's method
/// <c>calcAmountMST</c>
/// </remarks>
public display AmountMST amount()
{
    AmountMST ret;

    if (isConfigurationkeyEnabled(configurationKeyNum(LedgerBasic)))
    {
        if (hasFieldAccess(tableNum(LedgerJournalTrans), fieldNum(LedgerJournalTrans, AmountCurDebit))
            && hasFieldAccess(tableNum(LedgerJournalTrans), fieldNum(LedgerJournalTrans, AmountCurCredit)))
        {
            ret = ledgerJournalTrans.calcAmountMST();
        }
    }

    return ret;
}

/// <summary>
/// Returns the payment type of a particular payment.
/// </summary>
/// <returns>
/// An enum identification of the payment type.
/// </returns>
/// <remarks>
/// This method is called from <c>active</c> method of <c>LedgerJournalTrans</c> to
/// get the payment type by checking the<c>BankChequeNum</c> and <c>BankRemittanceFileId</c> fields.
/// </remarks>
public PaymentType getPaymentType()
{
    PaymentType paymentType;

    // Checks if the PaymentType is Promissory Note
    if (ledgerJournalTrans.BankChequeNum == ''
        && (ledgerJournalTrans.BankRemittanceFileId != ''
            || ledgerJournalTrans.BankPromissoryNoteNum != ''))
    {
        paymentType = PaymentType::PromissoryNote;
    }
    // Checks if the PaymentType is Check
    else if (ledgerJournalTrans.BankRemittanceFileId == ''
        && ledgerJournalTrans.BankPromissoryNoteNum == ''
        && ledgerJournalTrans.BankChequeNum != '')
    {
        paymentType = PaymentType::Check;
    }
    // Checks if the PaymentType is Electronic Transfer
    else if (ledgerJournalTrans.BankChequeNum == ''
        && ledgerJournalTrans.BankRemittanceFileId == ''
        && ledgerJournalTrans.BankPromissoryNoteNum == '')
    {
        paymentType = PaymentType::ElectronicPayment;
    }

    return paymentType;
}

/// <summary>
/// Gets the <c>VendTransBuffer</c> buffer for the selected payment.
/// </summary>
/// <param name="_recId">
/// The <c>recId</c> field that is used to find the <c>VendTransBuffer</c> buffer.
/// </param>
/// <returns>
/// The <c>VendTrans</c> buffer of a payment.
/// </returns>
/// <remarks>
/// This method is called from the <c>active</c> method of the <c>LedgerJournalTrans</c> table to
/// get <c>VendTrans</c> buffer.
/// </remarks>
public VendTrans getVendTransBuffer(RecId _recId)
{
    LedgerJournalTrans ledgerJournalTransLoc;

    ledgerJournalTransLoc = LedgerJournalTrans::findRecId(_recId, false);
    return VendTrans::findVoucherDate(ledgerJournalTransLoc.Voucher, ledgerJournalTransLoc.TransDate);
}

public void init()
{
    super();

    vendTable = this.args().record();
    vendAccount = vendTable.AccountNum;
}

/// <summary>
/// Opens the forms based on the payment type.
/// </summary>
/// <remarks>
/// This method is called from the clicked event of the <c>PaymentDisplay</c> button to open
/// <c>BankChequeTable</c>, <c>VendPromissoryNoteJour</c>, <c>BankAccountTrans</c> and
/// <c>LedgerJournalTable</c> forms for the payment type check, promissory note
/// and electronic payment respectively.
/// </remarks>
public void openForm()
{
    MenuFunction menuFunction;
    Args args = new Args();
    PaymentType paymentType = this.getPaymentType();

    switch (paymentType)
    {
        case PaymentType::Check:
            menuFunction = new MenuFunction(menuItemDisplayStr(BankChequeTable), MenuItemType::Display);
            args.record(ledgerJournalTrans);
            break;

        case PaymentType::PromissoryNote:
            menuFunction = new MenuFunction(menuItemDisplayStr(VendPromissoryNoteJour), MenuItemType::Display);
            break;

        case PaymentType::ElectronicPayment:
            if (LedgerJournalTable::find(ledgerJournalTrans.JournalNum).Posted == NoYes::Yes)
            {
                menuFunction = new MenuFunction(menuItemDisplayStr(BankAccountTrans), MenuItemType::Display);
            }
            else
            {
                menuFunction = new MenuFunction(menuItemDisplayStr(LedgerJournalTable5), MenuItemType::Display);
            }
            break;
    }

    if (menuFunction)
    {
        menuFunction.run(args);
    }
}

public int active()
{
    int ret;
    PaymentType paymentType;
    VendTrans vendTrans;

    ret = super();

    voucher = ledgerJournalTrans.Voucher;
    journalNumber = ledgerJournalTrans.JournalNum;
    paymentType = element.getPaymentType();
    vendTrans = element.getVendTransBuffer(ledgerJournalTrans.RecId);

    transDate = ledgerJournalTrans.TransDate;
    vendTransRecid = ledgerJournalTrans.VendTransId;

    if (voucher == '')
    {
        voucher = NullVoucher;
    }

    subledgerVoucherGeneralJournalEntry_ds.executeQuery();

    if (this.checkPaymentTypeForCheckAndElectronicPayment(paymentType) && (vendTransRecid != lastVendTransRecid || vendTransRecid == 0))
    {
        vendInvJourTmp_ds.initQueryforEPandCheckInvoices(ledgerJournalTrans);
        purchLineTmp_ds.initQueryforEPandCheckPurchaseOrder(ledgerJournalTrans.VendTransId);
        lastVendTransRecid = ledgerJournalTrans.VendTransId;
    }

    if (this.checkPaymentTypeForPromissoryNote(paymentType) && (vendTransRecid != lastVendTransRecid || vendTransRecid == 0))
    {
        vendInvJourTmp_ds.initQueryforPromissoryNoteInvoices(vendTrans);
        purchLineTmp_ds.initQueryforPromissoryNotePurchaseOrder(vendTrans);
        lastVendTransRecid = ledgerJournalTrans.VendTransId;
    }

    purchLineTmp_ds.executeQuery();
    vendInvJourTmp_ds.executeQuery();

    vendChequeCopy.enabled(ledgerJournalTrans.canPrintNonNegotiableCheque());

    return ret;
}

/// <summary>
///     Checks for the payment type for check or electronic payment.
/// </summary>
/// <param name="_paymentType">
///     Payment type.
/// </param>
/// <returns>
///     true if payment type is check or electronic payment; otherwise, false.
/// </returns>
protected boolean checkPaymentTypeForCheckAndElectronicPayment(PaymentType _paymentType)
{
    return (_paymentType == PaymentType::Check || _paymentType == PaymentType::ElectronicPayment);
}

/// <summary>
///     Checks for the payment type for promissory note.
/// </summary>
/// <param name="_paymentType">
///     Payment type.
/// </param>
/// <returns>
///     true if payment type is promissory note; otherwise, false.
/// </returns>
protected boolean checkPaymentTypeForPromissoryNote(PaymentType _paymentType)
{
    return (_paymentType == PaymentType::PromissoryNote);
}

/// <summary>
/// Adds ranges to the <c>LedgerJournalTrans</c> data source dynamically.
/// </summary>
/// <remarks>
/// This method adds range dynamically to the <c>TransDate</c> field to get the
/// payments from last 12 months.
/// </remarks>
public void executeQuery()
{
    VendInvoiceJour vendInvJour;
    VendSettlement vendSettlement, vendSettleBuffer;
    TransDate yearDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());

    str strRangeWithVoucher;

    switch (showCmb.selection())
    {
        case VendPaymentShow::Past12Months:
            ledgerJournalTransRangeTransDate.value(SysQueryRangeUtil::dateRange(prevYr(yearDate), yearDate));
            break;

        case VendPaymentShow::AllPayments:
            ledgerJournalTransRangeTransDate.value(SysQuery::valueUnlimited());
            break;
    }

    if (lookFor.valueStr() != '')
    {
        switch (searchByCmb.selection())
        {
            case VendPaymentSearchFor::Invoice:
                select firstonly LedgerVoucher from vendInvJour
                    where vendInvJour.InvoiceId == lookFor.valueStr()
                        && vendInvJour.InvoiceAccount == vendAccount;

                while select OffsetTransVoucher from vendSettleBuffer
                    join TransRecId from vendSettlement
                        where vendSettleBuffer.OffsetRecid == vendSettlement.TransRecId
                            && vendSettlement.OffsetTransVoucher == vendInvJour.LedgerVoucher
                            && vendSettleBuffer.AccountNum == vendAccount
                            && vendSettlement.AccountNum == vendAccount
                {
                    strRangeWithVoucher += strFmt('%1(%2 == "%3")',
                        (strRangeWithVoucher != '') ? '||' : '',
                        fieldStr(LedgerJournalTrans, Voucher),
                        vendSettleBuffer.OffsetTransVoucher);
                }

                if (strRangeWithVoucher != '')
                {
                    ledgerJournalTransRangeVoucher.value(strRangeWithVoucher);
                }
                else
                {
                    ledgerJournalTransRangeVoucher.value(queryValue(NullVoucher));
                }
                break;

            case VendPaymentSearchFor::PurchaseOrder:
                while select LedgerVoucher from vendInvJour
                    where vendInvJour.InvoiceAccount == vendAccount
                        && vendInvJour.PurchId == lookFor.valueStr()
                    join TransRecId from vendSettlement
                        where vendSettlement.OffsetTransVoucher == vendInvJour.LedgerVoucher
                            && vendSettlement.AccountNum == vendAccount
                    join OffsetTransVoucher from vendSettleBuffer
                        where vendSettleBuffer.OffsetRecid == vendSettlement.TransRecId
                            && vendSettleBuffer.AccountNum == vendAccount
                {
                    strRangeWithVoucher += strFmt('%1(%2 == "%3")',
                        (strRangeWithVoucher != '') ? '||' : '',
                        fieldStr(LedgerJournalTrans, Voucher),
                        vendSettleBuffer.OffsetTransVoucher);
                }

                if (strRangeWithVoucher != '')
                {
                    ledgerJournalTransRangeVoucher.value(strRangeWithVoucher);
                }
                else
                {
                    ledgerJournalTransRangeVoucher.value(queryValue(NullVoucher));
                }
                break;
        }
    }
    else
    {
        ledgerJournalTransRangeVoucher.value(SysQuery::valueUnlimited());
    }

    super();

    this.active();
}

/// <summary>
/// Adds ranges and creates a dynalink on the <c>LedgerJournalTrans</c> data source.
/// </summary>
public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceTable(tableNum(LedgerJournalTrans));
    qbds.clearDynalinks();

    // Add a link between the LedgerJournalTrans and the view, which will have a range on Vendor Account number.
    qbds.addDynalink(
        fieldNum(LedgerJournalTrans, RecId),
        ledgerJournalTransAccountView,
        fieldNum(LedgerJournalTransAccountView, RecId1));

    qbds.orderMode(OrderMode::OrderBy);
    qbds.addSortField(fieldNum(LedgerJournalTrans, TransDate));

    this.addOrUpdateQueryRanges();
}

/// <summary>
/// Reapplies filters after the user modifies the filter.
/// </summary>
public void reapplyFilters()
{
    if (this.queryRun() != null)
    {
        Query initQuery = new Query(this.query());

        // The queries can become different after the user modifies the ranges on the filter pane.
        // In order to keep those modifications, we have to update executed query.
        Query updatedQuery = new Query(this.queryRun().query());
        this.query(updatedQuery);
        this.addOrUpdateQueryRanges();

        this.executeQuery();

        // The filter pane reset removes the difference between queryRun and query. In order for the
        // reset to work, since they are now equivalent, we have to set the query back to the initial value
        // and then set the range values for the current quick filter controls.
        this.query(initQuery);
        this.addOrUpdateQueryRanges();
    }
}

private void addOrUpdateQueryRanges()
{
    QueryBuildDataSource qbds;
    QueryBuildRange transactionTypeRange;
    QueryBuildRange accountTypeRange;

    str transactionTypeRangeValue = strFmt(
        '((%1 != "") || (%2 != "") || (%3 != "") || (%4 == %5))',
        fieldStr(LedgerJournalTrans, BankChequeNum),
        fieldStr(LedgerJournalTrans, BankRemittanceFileId),
        fieldStr(LedgerJournalTrans, BankPromissoryNoteNum),
        fieldStr(LedgerJournalTrans, TransactionType),
        int2str(any2int(LedgerTransType::Payment)));

    qbds = this.query().dataSourceTable(tableNum(LedgerJournalTrans));

    transactionTypeRange = SysQuery::findOrCreateRange(qbds, fieldNum(LedgerJournalTrans, TransactionType));
    transactionTypeRange.value(transactionTypeRangeValue);
    transactionTypeRange.status(RangeStatus::Hidden);

    accountTypeRange = SysQuery::findOrCreateRange(qbds, fieldNum(LedgerJournalTrans, AccountType));
    accountTypeRange.value(queryValue(LedgerJournalACType::Vend));
    accountTypeRange.status(RangeStatus::Hidden);

    ledgerJournalTransRangeTransDate = SysQuery::findOrCreateRange(qbds, fieldNum(LedgerJournalTrans, TransDate));
    ledgerJournalTransRangeTransDate.status(RangeStatus::Hidden);

    ledgerJournalTransRangeVoucher = SysQuery::findOrCreateRange(qbds, fieldNum(LedgerJournalTrans, Voucher));
    ledgerJournalTransRangeVoucher.status(RangeStatus::Hidden);
}

public void init()
{
    super();

    generalJournalAccountEntry_ds.cacheAddMethod(tableMethodStr(GeneralJournalAccountEntry, displayAccountingExchangeRate));
}

public void executeQuery()
{
    QueryBuildDataSource qbds;
    QueryBuildRange qbr;

    QueryBuildDataSource subledgerVoucherGeneralJournalEntryDS = this.query().dataSourceTable(tableNum(SubledgerVoucherGeneralJournalEntry));
    QueryBuildRange subledgerVoucherGeneralJournalEntryGeneralJournalEntry =
        SysQuery::findOrCreateRange(subledgerVoucherGeneralJournalEntryDS, fieldNum(SubledgerVoucherGeneralJournalEntry, GeneralJournalEntry));

    qbds = this.query().dataSourceTable(tableNum(GeneralJournalEntry));
    qbr = qbds.addRange(fieldNum(GeneralJournalEntry, Ledger));
    qbr.value(SysQuery::value(Ledger::current()));

    qbr = qbds.addRange(fieldNum(GeneralJournalEntry, RecId));
    qbr.value(subledgerVoucherGeneralJournalEntryGeneralJournalEntry.value());

    // Apply range value
    if (journalNumber != '')
    {
        rangeLedgerEntryJournal.value(queryValue(journalNumber));
    }

    super();
}

/// <summary>
/// Adds ranges and creates a dynalink on the <c>LedgerJournalTrans</c> data source.
/// </summary>
public void init()
{
    super();

    this.query().dataSourceTable(tableNum(LedgerJournalTransAccountView)).clearDynalinks();

    this.query().dataSourceTable(tableNum(LedgerJournalTransAccountView)).addDynalink(
        fieldNum(LedgerJournalTransAccountView, AccountNum),
        vendTable,
        fieldNum(VendTable, AccountNum));
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = ledgerEntryJournal_ds.query().dataSourceTable(tableNum(LedgerEntryJournal));

    rangeLedgerEntryJournal = qbds.addRange(fieldNum(LedgerEntryJournal, JournalNumber));
    rangeLedgerEntryJournal.status(RangeStatus::Hidden);
}

public void executeQuery()
{
    QueryBuildDataSource qbds;
    QueryBuildRange qbr;

    qbds = this.query().dataSourceTable(tableNum(SubledgerVoucherGeneralJournalEntry));

    qbr = SysQuery::findOrCreateRange(qbds, fieldNum(SubledgerVoucherGeneralJournalEntry, Voucher));
    qbr.value(voucher);

    qbr = SysQuery::findOrCreateRange(qbds, fieldNum(SubledgerVoucherGeneralJournalEntry, AccountingDate));
    qbr.value(SysQuery::value(transDate));

    qbr = SysQuery::findOrCreateRange(qbds, fieldNum(SubledgerVoucherGeneralJournalEntry, VoucherDataAreaId));
    qbr.value(SysQuery::value(curext()));

    super();
}

