public void jumpRef()
{
    Args args = new Args();
    MenuFunction menuFunction;

    args = new Args(formStr(VendInvoiceJournal));
    menuFunction = new MenuFunction(menuItemDisplayStr(VendInvoiceJournal), MenuItemType::Display);

    if (vendInvJourTmp.SourceDocumentHeader)
    {
        args.record(VendInvoiceJour::findSourceDocumentHeader(vendInvJourTmp.SourceDocumentHeader));
    }
    else
    {
        args.record(VendInvoiceJour::findRecId(vendInvJourTmp.VendInvoiceJourRecId, false));
    }

    if (menuFunction != null)
    {
        changecompany(vendInvJourTmp.InvoiceCompany)
        {
            menuFunction.run(args);
        }
    }
}

public boolean modified()
{
    ledgerJournalTrans_ds.reapplyFilters();
    return super();
}

/// <summary>
/// Enables or disables the <c>SearchData</c> field.
/// </summary>
/// <returns>
/// true if the value is modified; otherwise, false
/// </returns>
public boolean modified()
{
    if (searchByCmb.selection() == 0)
    {
        lookFor.text('');
        lookFor.allowEdit(false);
    }
    else
    {
        lookFor.allowEdit(true);
    }

    return super();
}

public boolean modified()
{
    boolean ret = super();

    ledgerJournalTrans_ds.reapplyFilters();

    return ret;
}

