}

public void init()
{
    additionalDimsNeededWarningImageResource = ImageReference::constructForSymbol('Warning', "@WAX:CatchWeightTagDiscrepanciesDimWarning").pack();

    super();

    xSysLastValue::getLast(this);

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

