public void createOrClearQueryBuildRange(
    QueryBuildDataSource    _qbds,
    FieldId                 _fieldId,
    str                     _rangeValue)
{
    if (_rangeValue)
    {
        SysQuery::findOrCreateRange(_qbds, _fieldId).value(queryValue(_rangeValue));
    }
    else
    {
        _qbds.clearRange(_fieldId);
    }
}

