public void run()
{
    super();

    boolean loadData = this.mustAutoLoadDataBasedOnSettings();

    if (loadData)
    {
        InventSum_DS.executeQuery();
    }
}

public boolean unpack(container _packedClass)
{
    boolean     ret;
    int         version;
    int         autoDataRefresh;
    int         discrepanciesOnly;

    version = conPeek(_packedClass, 1);

    switch (version)
    {
        case #CurrentVersion:
            [version, autoDataRefresh, discrepanciesOnly] = _packedClass;
            ret = true;
            break;

        default :
            ret = false;
            break;
    }

    AutomaticDataRefresh.value(autoDataRefresh);
    this.setLimitToDiscrepanciesOnly(discrepanciesOnly);

    return ret;
}

InventDimCtrl_Frm_OnHandMovement inventDimSetupObject()
{
    if (!inventDimFormSetup)
    {
        inventDimFormSetup = InventDimCtrl_Frm_OnHandMovement::newFromForm(element);
        inventDimFormSetup.parmLargeRightClickForm(NoYes::No);
    }

    return inventDimFormSetup;
}

