public int task(int _taskId)
{
    int ret;

    #Task
    switch (_taskId)
    {
        case #TaskRefresh:
            inventSum_DS.executeQuery();
            return ret;
    }

    ret = super(_taskId);

    InventSum_ds.reread();
    InventSum_ds.refresh();

    return ret;
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
        case InventDimFormDesignUpdate::LinkActive:
            element.inventDimSetupObject().initFromCaller();
            element.inventDimSetupObject().parmCtrlItemId(inventSum_ItemId);
            // No break

        case InventDimFormDesignUpdate::Active:
            element.inventDimSetupObject().formActiveSetup();
            element.inventDimSetupObject().formSetControls(true, true);
            break;

        default:
            throw error(strfmt("@SYS54195", funcname()));
    }
}

void searchForDiscrepancies()
{
    // Only do full search if ItemId filter is open
    if (!ItemId.valueStr())
    {
        searchItemId = ItemId.valueStr();

        searchInventDim.InventSiteId = InventSiteId.valueStr();
        searchInventDim.InventLocationId = InventLocationId.valueStr();
        searchInventDim = InventDim::findOrCreate(searchInventDim);

        WHSCatchWeightTagFullDimDiscrepanciesSearch searcher = new WHSCatchWeightTagFullDimDiscrepanciesSearch();
        searcher.parmSearchItemId(searchItemId);
        searcher.parmSearchInventDim(searchInventDim);
        searcher.search(WHSCatchWeightTagDiscrepanciesTmp_ds.cursor());

        WHSCatchWeightTagDiscrepanciesTmp_ds.linkType(FormLinkType::ExistJoin);
        InventSum_ds.query().dataSourceTable(tableNum(WHSCatchWeightTagDiscrepanciesTmp)).joinMode(JoinMode::ExistsJoin);

        discrepanciesTmpLoaded = true;
        showFilterChangeWarning = true;
    }

    this.setLimitToDiscrepanciesOnly(true);

    InventSum_ds.executeQuery();
}

void clearSearchForDiscrepancies()
{
    WHSCatchWeightTagDiscrepanciesTmp DiscrepanciesTmp;

    DiscrepanciesTmp.linkPhysicalTableInstance(WHSCatchWeightTagDiscrepanciesTmp_ds.cursor());

    delete_from DiscrepanciesTmp;

    WHSCatchWeightTagDiscrepanciesTmp_ds.linkType(FormLinkType::NotExistJoin);
    InventSum_ds.query().dataSourceTable(tableNum(WHSCatchWeightTagDiscrepanciesTmp)).joinMode(JoinMode::NoExistsJoin);

    discrepanciesTmpLoaded = false;
}

void warnOnFilterChangedAfterSearchLoaded()
{
    if (discrepanciesTmpLoaded)
    {
        if ((!searchItemId || ItemId.valueStr() == searchItemId)
            && (!searchInventDim.InventSiteId || InventSiteId.valueStr() == searchInventDim.InventSiteId)
            && (!searchInventDim.InventLocationId || InventLocationId.valueStr() == searchInventDim.InventLocationId))
        {
            showFilterChangeWarning = true;
        }
        else if (showFilterChangeWarning)
        {
            warning(strFmt("@WAX:CatchWeightTagDiscrepanciesCriteriaChangeWarning", FindDiscrepanciesButton.labelText()));

            showFilterChangeWarning = false;
        }
    }
}

public void executeQuery()
{
    QueryBuildDataSource qbdsInventDim;

    qbdsInventDim = inventDim_DS.queryBuildDataSource();

    element.createOrClearQueryBuildRange(qbdsInventDim, fieldNum(InventDim,InventSiteId), InventSiteId.valueStr());
    element.createOrClearQueryBuildRange(qbdsInventDim, fieldNum(InventDim,InventLocationId), InventLocationId.valueStr());

    element.inventDimSetupObject().modifyQuery(inventSum_DS, inventDim_DS);

    qbdsInventDim.addSortField(fieldNum(InventDim, InventStatusId));

    element.createOrClearQueryBuildRange(inventSum_DS.queryBuildDataSource(), fieldNum(InventSum, ItemId), ItemId.valueStr());

    if (!discrepancyRange)
    {
        QueryBuildDataSource dsCatchWeightTagSummary = this.query().dataSourceTable(tableNum(WHSCatchWeightTagSummary));
        discrepancyRange = dsCatchWeightTagSummary.addRange(fieldNum(WHSCatchWeightTagSummary, RecId));
        discrepancyRange.status(HideQueryRangeStatus);
        discrepancyRange.value(strFmt('((%1.%2 != %4.%5) || (%1.%3 != %4.%6))',
                tableStr(InventSum),
                fieldStr(InventSum, PdsCWPhysicalInvent),
                fieldStr(InventSum, PhysicalInvent),
                tableStr(WHSCatchWeightTagSummary),
                fieldStr(WHSCatchWeightTagSummary, TagCount),
                fieldStr(WHSCatchWeightTagSummary, SumCapturedWeight)));
    }

    if (element.mustLimitToDiscrepanciesOnly())
    {
        this.query().dataSourceName(WHSCatchWeightTagSummaryFilter_ds.name()).enabled(true);
        discrepancyRange.enabled(true);
    }
    else
    {
        element.clearSearchForDiscrepancies();
        this.query().dataSourceName(WHSCatchWeightTagSummaryFilter_ds.name()).enabled(false);
        discrepancyRange.enabled(false);
    }

    element.warnOnFilterChangedAfterSearchLoaded();

    super();
}

public void init()
{
    super();

    element.inventDimSetupObject().initQueryInventSum(inventSum_DS);

    inventSum_DS.queryBuildDataSource().addRange(fieldNum(InventSum, PhysicalInvent)).value(SysQuery::valueNot(0));
    inventSum_DS.queryBuildDataSource().addRange(fieldNum(InventSum, ClosedQty)).value(SysQuery::valueNot(NoYes::No));

    QueryBuildDataSource whsInventTableDS = this.queryBuildDataSource().addDataSource(tableNum(WHSInventTable));
    whsInventTableDS.joinMode(JoinMode::ExistsJoin);
    whsInventTableDS.addLink(fieldNum(InventSum, ItemId), fieldNum(WHSInventTable, ItemId));

    QueryBuildRange policyNameRange = whsInventTableDS.addRange(fieldNum(WHSInventTable, CatchWeightItemHandlingPolicyName));
    policyNameRange.status(HideQueryRangeStatus);
    policyNameRange.value(SysQuery::valueNot(''));

    QueryBuildDataSource policyDS = whsInventTableDS.addDataSource(tableNum(WHSCatchWeightItemHandlingPolicy));
    policyDS.joinMode(JoinMode::InnerJoin);
    policyDS.relations(true);

    QueryBuildRange tagTrackingRange = policyDS.addRange(fieldNum(WHSCatchWeightItemHandlingPolicy, CatchWeightTagTracking));
    tagTrackingRange.status(HideQueryRangeStatus);
    tagTrackingRange.value(SysQuery::value(NoYes::Yes));

    QueryBuildRange tagDimensionTrackingRange = policyDS.addRange(fieldNum(WHSCatchWeightItemHandlingPolicy, CatchWeightTagDimensionTrackingMethod));
    tagDimensionTrackingRange.status(HideQueryRangeStatus);
    tagDimensionTrackingRange.value(SysQuery::value(WHSCatchWeightTagDimensionTrackingMethod::ProductTrackingAndAllStorageDimensions));

    this.cacheAddMethod(tableMethodStr(InventSum, itemName));
}

