/// <summary>
/// Gets and image if the selected records show that the total count and weights exactly match during discrepancy display indicating that more dimensions are needed
/// to see actual differences.
/// </summary>
/// <param name="_inventSum">
/// The <c>InventSum</c> table buffer from the datasource.
/// </param>
/// <returns>
/// A warning icon if the dimension setup needs to be further defined to see discrepancies; otherwise, a blank icon.
/// </returns>
public display container displayInvalidDimensionWarning(InventSum _inventSum)
{
    container ret;

    if (element.mustLimitToDiscrepanciesOnly())
    {
        WHSCatchWeightTagSummary joinedTagSummary = Global::formJoinedRecord(_inventSum, WHSCatchWeightTagSummary_ds);

        if (_inventSum.PdsCWPhysicalInvent == joinedTagSummary.TagCount && _inventSum.PhysicalInvent == joinedTagSummary.SumCapturedWeight)
        {
            ret = additionalDimsNeededWarningImageResource;
        }
    }

    return ret;
}

