/// <summary>
/// Executes the query on the active supply/demand tab.
/// </summary>
/// <param name = "_resetActivePageQueryFlagOnly">Determines whether active page query flag should only be refreshed to the currently active supply/demand tab.</param>
/// <returns>The number of rows loaded by the data source.</returns>
public int executeQueryActivePage(boolean _resetActivePageQueryFlagOnly = false)
{
    int numberOfRows = 0; 

    if (purchLines.isActivePage())
    {
        purchLine_ds.executeQuery();
        numberOfRows = purchLine_ds.numberOfRowsLoaded();
        queryPurchLine = false;
    }
    else if (transportRequestLines.isActivePage())
    {
        tmsTransportRequestLine_ds.executeQuery();
        numberOfRows = tmsTransportRequestLine_ds.numberOfRowsLoaded();
        queryTMSTransportRequestLine = false;
    }

    if (!_resetActivePageQueryFlagOnly)
    {
        queryPurchLine      = !purchLines.isActivePage();            
        queryTMSTransportRequestLine        = !transportRequestLines.isActivePage();
    }

    return numberOfRows;
}

public int researchActivePage(boolean _resetActivePageQueryFlagOnly = false)
{
    int numberOfRows = 0;

    if (purchLines.isActivePage())
    {
        purchLine_ds.research(true);
        numberOfRows = purchLine_ds.numberOfRowsLoaded();
        queryPurchLine = false;
    }
    else if (transportRequestLines.isActivePage())
    {
        tmsTransportRequestLine_ds.research(true);
        numberOfRows = tmsTransportRequestLine_ds.numberOfRowsLoaded();
        queryTMSTransportRequestLine = false;
    }

    if (!_resetActivePageQueryFlagOnly)
    {
        queryPurchLine                  = !purchLines.isActivePage();
        queryTMSTransportRequestLine    = !transportRequestLines.isActivePage();
    }

    return numberOfRows;
}

protected boolean restrictToSameWarehouseWhenCreatingLoads()
{
    return true;
}

protected void createTmpLoadLines()
{
    WHSITmpLoadLineCreator tmpLoadLineCreator = loadPlanningWorkbenchForm;

    if (loadPlanningWorkbenchForm.parmAddOrder())
    {
        tmpLoadLineCreator = WHSOncePerOrderTmpLoadLineCreator::newFromTmpLoadLineCreator(tmpLoadLineCreator);
    }

    loadPlanningWorkbenchForm.parmLoadPlanningWorkbenchServerForm().clearGlobalVariables();

    switch (true)
    {
        case purchLines.isActivePage():
            boolean restrictToSameWarehouseWhenCreatingLoads = this.restrictToSameWarehouseWhenCreatingLoads();
            int numberOfProcessedLines;
            // current invent location id takes value from the first purchase order line if purchase lines will be added to a new load, 
            // or value from existing load if records will be added to existing load
            InventLocationId currentInventLocationId = loadPlanningWorkbenchForm.parmExistingLoadInventLocationId() ?
                loadPlanningWorkbenchForm.parmExistingLoadInventLocationId() :
                InventDim::find(purchLine.InventDimId).InventLocationId;
            boolean showWarningForDifferentWHS;

            for (PurchLine purchLineRecord = purchLine_ds.getFirst(true) ? purchLine_ds.getFirst(true) : purchLine_ds.cursor();
                purchLineRecord;
                purchLineRecord = purchLine_ds.getNext())
            {
                if (!restrictToSameWarehouseWhenCreatingLoads || InventDim::find(purchLineRecord.inventDimId).InventLocationId == currentInventLocationId)
                {
                    tmpLoadLineCreator.createTmpLoadLinesFromPurchaseLine(purchLineRecord, numberOfProcessedLines);
                    ++numberOfProcessedLines;
                }
                else
                {
                    showWarningForDifferentWHS = true;
                }
            }

            if (showWarningForDifferentWHS)
            {
                warning(strFmt("@WAX:LPWDifferentWarehousesWarning", currentInventLocationId));
            }
            break;

        case transportRequestLines.isActivePage():
            for (TMSTransportRequestLine transportRequestLineRecord = tmsTransportRequestLine_ds.getFirst(true) ? tmsTransportRequestLine_ds.getFirst(true) : tmsTransportRequestLine_ds.cursor();
                transportRequestLineRecord;
                transportRequestLineRecord = tmsTransportRequestLine_ds.getNext())
            {
                // ensures that the Pays Freight on the associated orders' dlvTerms are in sync
                tmpLoadLineCreator.createTmpLoadLinesTransportRequestLines(transportRequestLineRecord);
            }
            break;
    }
}

