/// <summary>
///    Adds filter to load id range
/// </summary>
/// <param name="_orderNum">
///    The purchase id, sales id, or transfer id tied to load
/// </param>
/// <param name="_inventTransType">
/// The inventTransType for the order.
/// </param>
protected void filterLoadGrid(
    Num             _orderNum,
    InventTransType _inventTransType)
{
    if (_orderNum
    &&  _inventTransType == InventTransType::Purch)
    {
        boolean existsPurchLineWithNonZeroQtyLeftToLoad = WHSPurchLine::existsPurchLineWithNonZeroQtyLeftToLoad(_orderNum);

        WHSLoadLine locLoadLine;

        if (!existsPurchLineWithNonZeroQtyLeftToLoad)
        {
            select firstonly InventTransId, OrderNum, InventTransType, TransportRequest from locLoadLine
                where locLoadLine.OrderNum          == _orderNum
                   && locLoadLine.InventTransType   == InventTransType::Purch
                   && locLoadLine.TransportRequest  == NoYes::No
                   && locLoadLine.LoadId            == '';
        }

        this.applyLoadIdFilter(_orderNum, existsPurchLineWithNonZeroQtyLeftToLoad || locLoadLine);
    }
}

