protected void setFormInternalState()
{
    if (element.args().record())
    {
        Common comm = element.args().record();

        switch (element.args().dataset())
        {
            case tableNum(PurchTable):
                purchId = comm.(fieldNum(PurchTable, PurchId));
                break;

            case tableNum(WHSLoadTable):
                filterLoadId = comm.(fieldNum(WHSLoadTable, LoadId));
                break;
        }
    }
}

private date defaultReceiptDateFilterValue()
{
    return DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone());
