public void init()
{
    loadPlanningWorkbenchForm = WHSLoadPlanningWorkbenchForm::newFromArgs(element.args());
    whsLoadPlanningListPageForm = new WHSLoadPlanningListPageForm();

    this.setFormInternalState();

    xSysLastValue::getLast(this);

    super();

    if (purchId)
    {
        hideShippedOrders = false;
    }
    else
    {
        hideShippedOrders = true;
    }

    if (!element.isOpenedInOrderContext())
    {
        // Filters should only be applied if no order is passed, 
        // as otherwise the order risk not to show up in case the site/warehouse do not match the filters.
        InventSiteIdFilter.text(lpwFilterInventSiteId);
        InventLocationIdFilter.text(lpwFilterInventLocationId);

        ReceiptDateFilter.dateValue(element.defaultReceiptDateFilterValue());
    }

    element.updateDesign_Load(InventDimFormDesignUpdate::Init);

    boolean inTransitPlanning = TMSParameters::find().InTransitPlanning;
    transportRequestLines.visible(inTransitPlanning);
    separatorHubs.visible(inTransitPlanning);
    overrideLocation.visible(inTransitPlanning);

    btnPostShipTransfer.visible(whsLoadPlanningListPageForm.shouldBtnPostShipTransferBeVisible());

    HideShipped.value(hideShippedOrders);

    if (element.args().parmEnumType() == enumNum(TMSLoadBuildSupplyDemandType))
    {
        supplyDemandTypeSet = true;
        supplyDemandType = element.args().parmEnum();
    }

    this.setQueriesForDemandType();
    this.setValidTimeStateDateTimeRangeOnQueryDataSources();
    this.assignOrigQueries();

    queryPurchLine = true;
    queryTMSTransportRequestLine = true;
}

private void setValidTimeStateDateTimeRangeOnQueryDataSources()
{
    whsLoadTable_ds.query(new QueryRun(queryStr(WHSLPWFilterLoad)).query());
    whsLoadTable_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(), DateTimeUtil::maxValue());
    purchAddress_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(), DateTimeUtil::maxValue());        
}

