/// <summary>
///  Re-executes queries for appropriate datasources
/// </summary>
/// <param name = "_filterType">Applied filter type</param>
public void reExecuteQueries(WHSLPWFilterType _filterType)
{
    switch (_filterType)
    {
        case WHSLPWFilterType::Load:
            whsLoadTable_ds.executeQuery();
            whsLoadLine_ds.executeQuery();
            break;

        case WHSLPWFilterType::PurchOrder:
            purchLine_ds.executeQuery();
            queryPurchLine = false;
            break;
    }
}

protected WHSLPWFilterType getLPWFilterType()
{
    return WHSLPWFilterType::PurchOrder;
}

public void restoreQuery(WHSLPWFilterType _filterType)
{
    element.updateQueries(_filterType);
    element.reExecuteQueries(_filterType);
}

/// <summary>
/// Updates the form datasource queries.
/// </summary>
/// <param name = "_filterType">A <c>WHSLPWFilterType</c> value used to determine the datasource to update its query.</param>
protected void updateQueries(WHSLPWFilterType _filterType)
{
    switch (_filterType)
    {
        case WHSLPWFilterType::Load:
            if (origQueryLoads)
            {
                whsLoadTable_ds.query(new QueryRun(origQueryLoads).query());
            }
            break;

        case WHSLPWFilterType::PurchOrder:
            if (origQueryPurch)
            {
                purchLine_ds.query(new QueryRun(origQueryPurch).query());
            }
            break;
    }
}

[Wrappable(true)]
internal void activatePage()
{
    if (this.mustActivatePurchLinesTab())
    {
        this.activatePurchLinesPage();
    }
    else if (WHSInboundLoadPlanningWorkbenchActivateDefaultFilterFlight::instance().isEnabled())
    {
        // This is the default value and should always be last.
        purchLines.pageActivated();
    }
}

private boolean mustActivatePurchLinesTab()
{
    return (purchId || filterLoadId || (supplyDemandTypeSet && supplyDemandType == TMSLoadBuildSupplyDemandType::Purchase 
        && WHSInboundLoadPlanningWorkbenchActivateDefaultFilterFlight::instance().isEnabled()));
}

private void activatePurchLinesPage()
{
    if (WHSInboundLoadPlanningWorkbenchActivateDefaultFilterFlight::instance().isEnabled())
    {
        purchLines.activatePage();

        if ((purchId || supplyDemandTypeSet)
            && WHSInboundLoadPlanningWorkbenchDefaultFilterFromPurchTableFlight::instance().isEnabled())
        {
            purchLines.pageActivated();
        }
    }
    else
    {
        purchLines.pageActivated();
    }       
}

public void run()
{
    super();

    this.activatePage();        

    if (LoadLPWFilter.text() != lpwFilterCodeLoads)
    {
        LoadLPWFilter.text(lpwFilterCodeLoads);
        LoadLPWFilter.modified();
    }
}

public boolean unpack(container _packedClass)
 {
     boolean ret;
     int version = conPeek(_packedClass, 1);

     switch (version)
     {
         case #CurrentVersion:
             [version, #CurrentList] = _packedClass;
             ret = true;
             break;
     }

     return ret;
 }

private void updateDesign_Load(InventDimFormDesignUpdate _mode)
{
    InventDimParm inventDimParmLocal;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetupLoad)
            {
                inventDimFormSetupLoad = InventDimCtrl::newFromFormValidDataSource(element, inventDimLoad_ds.name());
                inventDimParmLocal.setAllProductDimensions();
                inventDimFormSetupLoad.parmDimParmVisibleGrid(inventDimParmLocal);
            }

        case InventDimFormDesignUpdate::Active:
            inventDimFormSetupLoad.formActiveSetup();
            inventDimFormSetupLoad.formSetControls(true);
            break;

        default:
            throw error(strFmt("@SYS54195", funcName()));
    }
}

private void updateDesign_Purchase(InventDimFormDesignUpdate _mode)
{
    InventDimParm inventDimParmLocal;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetupPurchase)
            {
                inventDimFormSetupPurchase = InventDimCtrl::newFromFormValidDataSource(element, inventDimPurch_ds.name());
                inventDimParmLocal.setAllInventDim();
                inventDimFormSetupPurchase.parmDimParmVisibleGrid(inventDimParmLocal);
            }

        case InventDimFormDesignUpdate::Active:
            inventDimFormSetupPurchase.formActiveSetup();
            inventDimFormSetupPurchase.formSetControls(true);
            break;

        default:
            throw error(strFmt("@SYS54195", funcName()));
    }
}

