}

/// <summary>
/// Refreshes the form after adding supply/demand to a load.
/// </summary>
/// <param name = "_whsLoadTable">Load table buffer to be selected in the grid; Emtpy buffer otherwise.</param>
/// <param name = "_forceLoadsRefresh">Determines whether to force refresh load list.</param>
protected void refreshAfterAddingToLoad(WHSLoadTable _whsLoadTable, boolean _forceLoadsRefresh)
{
    int loadedRecordCount = isWHSILPWRefreshAfterAddingToLoadFlightEnabled ? this.researchActivePage() : this.executeQueryActivePage();

    if ((loadedRecordCount == 0 && this.isOpenedInOrderContext())
        || _forceLoadsRefresh)
    {
        if (lpwFilterCodeLoads && lpwFilterCodeLoads == LoadLPWFilter.text())
        {
            // filter set
            WHSLoadPlanningWorkbenchFilter filterTable = WHSLoadPlanningWorkbenchFilter::find(lpwFilterCodeLoads, WHSLPWFilterType::Load);
            whsLoadTable_ds.query(new QueryRun(filterTable.FilterQuery).query());

            element.reExecuteQueries(WHSLPWFilterType::Load);
        }
        else
        {
            LoadLPWFilter.text('');
            this.restoreQuery(WHSLPWFilterType::Load);               
        }

    }

    //we need to re-read and re-position if we have a recId since we could be adding a shipment to and existing load which we want to display the latest data from
    if (_whsLoadTable.RecId != 0)
    {
        WHSLoadTable_DS.reread();
        WHSLoadTable_DS.positionToRecord(_whsLoadTable);
    }
}

