protected void setValuesBasedOnFilterTable(WHSLoadPlanningWorkbenchFilter _filterTable)
{
    switch (_filterTable.FilterType)
    {
        case WHSLPWFilterType::PurchOrder:
            lpwFilterCodePurch = _filterTable.FilterCode;
            purchLine_ds.query(new QueryRun(_filterTable.FilterQuery).query());
            break;
    }
}

protected void restoreQueryOnFilterModifiedWithBlankFilter(WHSLPWFilterType _filterType)
{
    switch (_filterType)
    {
        case WHSLPWFilterType::PurchOrder:
            lpwFilterCodePurch = '';
            element.restoreQuery(WHSLPWFilterType::PurchOrder);
            break;
    }
}

private void enableTMSFields(boolean _enable)
{
    whsLoadTable_CarrierCode.allowEdit(_enable);
    whsLoadTable_CarrierServiceCode.allowEdit(_enable);
    whsLoadTable_CarrierGroupCode.allowEdit(_enable);
    whsLoadTable_ModeCode.allowEdit(_enable && whsLoadTable.CarrierCode == '');
    whsLoadTable_BrokerCode.allowEdit(_enable);
}

private void enableTMSActions(boolean _enable)
{
    TMSRateRouteWorkbench.enabled(_enable);
    RateRouteWorkbench.enabled(_enable);
    TMSLoadBuildWorkbench.enabled(_enable);
    RatingRouting.enabled(_enable);
    TransportBtn.enabled(_enable);
    WHSBillOfLading.enabled(_enable);            
    TMSFregihtBillDetail.enabled(_enable);
    TMSTransportationStatus.enabled(_enable);
    TMSLoadSeal.enabled(_enable);

    TMSInvoiceJournal.enabled(_enable && TMSFreightBillDetail::existsInvoiceJournal(whsLoadTable.RouteCode, whsLoadTable.LoadId));            
    manualRoute.enabled(_enable && whsLoadTable.RouteCode == '');
    TransportationTenders.enabled(_enable && whsLoadTable.isAssociableToTender());
    addRoute.enabled(_enable 
                     && !whsLoadTable.RouteCode 
                     && whsLoadTable.LoadDirection == WHSLoadDirection::Inbound);
}

public int active()
{
    int ret = super();

    WHSLoadLine loadLine = loadPlanningWorkbenchForm.selectLoadLine(whsLoadTable);

    btnInboundShipConfirm.enabled(WHSPostPackingSlip::isShipConfirmEnabled(whsLoadTable));
    btnProductReceipt.enabled(WHSPostPackingSlip::isPackingSlipEnabled(whsLoadTable));
    btnBillOfLading.enabled(WHSShipConfirm::isBOLEnabledForLoad(whsLoadTable));
    btnReverseShipConfirm.enabled(WHSReverseShipConfirm::canShipConfirmationBeReversed(whsLoadTable));
    btnPostShipTransfer.enabled(WHSShipConfirm::canShipTransferOrder(whsLoadTable));
    btnCancelProductReceipt.enabled(WHSReverseShipConfirm::canCancelProductReceipt(whsLoadTable));
    btnInboundReceivingCompleted.enabled(whsLoadTable.canCompleteReceiving());
    btnInboundReverseReceivingCompleted.enabled(whsLoadTable.canReverseReceivingCompleted());

    WHSPackStructureCrossDockCreate.enabled(whsLoadTable.canCreateCrossDockPackingStructure());
    change.enabled(whsLoadTable.LoadStatus != WHSLoadStatus::Open);

    this.enableTMSFields(whsLoadTable.LoadStatus != WHSLoadStatus::Shipped);

    this.enableTMSActions(whsLoadTable.canUseTMSActions());

    whsLoadTable_ds.allowEdit(whsLoadTable.LoadStatus < WHSLoadStatus::Shipped);
    whsLoadTable_ds.allowDelete(whsLoadTable.LoadStatus < WHSLoadStatus::Shipped);
    whsLoadLine_ds.allowEdit(whsLoadTable.LoadStatus < WHSLoadStatus::Shipped);
    whsLoadLine_ds.allowDelete(whsLoadTable.LoadStatus < WHSLoadStatus::Shipped);

    return ret;
}

public void executeQuery()
{
    element.filterLoadDirection();

    element.filterLoadsOnOrderType();

    QueryBuildDataSource qbdsLoadTable = WHSLoadTable_DS.queryBuildDataSource();

    if (filterLoadId)
    {
        SysQuery::findOrCreateRange(qbdsLoadTable, fieldNum(WHSLoadTable, LoadId)).value(filterLoadId);
    }

    str loadStatusRange = hideShippedOrders ? strFmt('<%1', WHSLoadStatus::Shipped) : SysQuery::valueUnlimited();
    SysQuery::findOrCreateRange(qbdsLoadTable, fieldNum(WHSLoadTable, LoadStatus)).value(loadStatusRange);

    super();
}

