public void executeQuery()
{
    if (purchId)
    {
        SysQuery::findOrCreateRange(purchLine_ds.query().dataSourceTable(tableNum(PurchLine)), fieldNum(PurchLine, PurchId)).value(queryValue(purchId));
    }

    if (DirectFilters.enabled())
    {
        element.applySiteWHFilters(purchLine_ds);

        loadPlanningWorkbenchForm.applyDeliveryDateFilter(purchLine_ds.query().dataSourceTable(tableNum(PurchLine)), ReceiptDateFilter.dateValue());
    }

    QueryBuildDataSource inventTransOriginQbds = SysQuery::findOrCreateDataSource(purchLine_ds.query(), tableNum(InventTransOrigin), tableNum(PurchLine));
    inventTransOriginQbds.joinMode(JoinMode::ExistsJoin);
    inventTransOriginQbds.relations(true);

    QueryBuildDataSource inventTransQbds = SysQuery::findOrCreateDataSource(purchLine_ds.query(), tableNum(InventTrans), tableNum(PurchLine));
    inventTransQbds.joinMode(JoinMode::ExistsJoin);
    inventTransQbds.relations(true);
    SysQuery::findOrCreateRange(inventTransQbds, fieldNum(InventTrans, StatusReceipt)).value(SysQuery::value(StatusReceipt::Ordered));            

    super();
}

[SysClientCacheDataMethodAttribute(true)]
public display Volume displayVolumePurch(PurchLine _purchLine)
{
    Volume unitVolume = _purchLine.whsItemUnitVolume();

    if (unitVolume == 0)
    {
        return 0;
    }

    WHSQtyLeftToLoad qtyLeftToLoad = WHSPurchLine::find(_purchLine.InventTransId).inboundHandlingQtyLeftToLoad();
    return unitVolume * qtyLeftToLoad;
}

[SysClientCacheDataMethodAttribute(true)]
public display Weight displayWeightPurch(PurchLine _purchLine)
{
    ItemGrossWeight grossWeight = _purchLine.whsItemGrossWeight();

    if (grossWeight == 0)
    {
        return 0;
    }

    WHSQtyLeftToLoad qtyLeftToLoad = WHSPurchLine::find(_purchLine.InventTransId).inboundHandlingQtyLeftToLoad();
    return grossWeight * qtyLeftToLoad;
}

[SysClientCacheDataMethodAttribute(true)]
public display WHSQtyLeftToLoad inboundQtyLeftToLoad(PurchLine _purchLine)
{
    return WHSPurchLine::find(_purchLine.InventTransId).inboundHandlingQtyLeftToLoad();
}

[SysClientCacheDataMethodAttribute(true)]
public display PdsCWUnitId displayCatchWeightUOM(PurchLine _purchLine)
{
    return PdsCatchWeightItem::pdsCWUnitId(_purchLine.ItemId);
}

[SysClientCacheDataMethodAttribute(true)]
public display InventUnitId displayInventUOM(PurchLine _purchLine)
{
    return InventTableModule::find(_purchLine.ItemId, ModuleInventPurchSales::Invent).UnitId;
}

[SysClientCacheDataMethodAttribute(true)]
public display UnitOfMeasureSymbol displayHandlingUOM(PurchLine _purchLine)
{
    return WHSCatchWeightHelper::inventHandlingUnitId(_purchLine.ItemId);
}

