public boolean modified()
{
    boolean ret = super();

    WHSLPWFilterCode filterCode = this.valueStr();

    if (filterCode)
    {
        WHSLoadPlanningWorkbenchFilter filterTable = WHSLoadPlanningWorkbenchFilter::find(filterCode, element.getLPWFilterType());

        if (filterTable)
        {
            element.setValuesBasedOnFilterTable(filterTable);

            boolean directFiltersPreviouslySet = InventSiteIdFilter.text() || InventLocationIdFilter.text() || ShipDateFilter.dateValue() || ReceiptDateFilter.dateValue();

            if (directFiltersPreviouslySet)
            {
                InventSiteIdFilter.text('');
                InventLocationIdFilter.text('');
                ShipDateFilter.dateValue(dateNull());
                ReceiptDateFilter.dateValue(dateNull());
            }

            DirectFilters.enabled(false);

            element.reExecuteQueries(filterTable.FilterType);
        }
        else
        {
            ret = false;
        }
    }
    else
    {
        DirectFilters.enabled(true);

        element.restoreQueryOnFilterModifiedWithBlankFilter(element.getLPWFilterType());
    }

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        if (InventSiteIdFilter.text()
            && inventlocationIdFilter.text()
            && InventLocation::find(inventlocationIdFilter.text()).InventSiteId != InventSiteIdFilter.text())
        {
            inventlocationIdFilter.text('');                    
        }

        element.siteWHFiltersChanged();
    }

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        if (InventLocationIdFilter.text())
        {
            inventSiteIdFilter.text(InventLocation::find(InventLocationIdFilter.text()).InventSiteId);
        }

        element.siteWHFiltersChanged();
    }

    return ret;
}

