[Form]
public class WHSKanbanWaveTableManagePickingListPool extends FormRun
{
    QueryBuildRange                    curWaveRange;
    WHSWaveId                          waveId;
    WHSKanbanWaveTableManagePLPoolForm whsKanbanWaveTableManagePLPoolForm;
    InventDimCtrl_Frm_WHS              inventDimFormSetup;
    WHSImageListAppl_WHS               imageList;

}

public boolean canClose()
{
    boolean ret;

    ret = super();

    if (whsWaveTable.RecId && !whsWaveTable.WaveTemplateName && whsKanbanWaveLine.RecId)
    {
        ret = checkFailed("@WAX3334");
    }

    return ret;
}

public void close()
{
    whsKanbanWaveTableManagePLPoolForm.deleteWaveTable(whsWaveTable);

    if (formHasMethod(element.args().caller(), identifierStr(doRefresh)))
    {
        element.args().caller().doRefresh();
    }

    super();
}

/// <summary>
/// Controls activation of 'Add' and 'Remove' buttons based on wave and wave pool.
/// </summary>
public void enableAddRemoveButtons()
{
    boolean canAddLineToWave        = whsKanbanWaveTableManagePLPoolForm.canAddLineToWave(whsWaveTable, whsKanbanJobPickingListPool);
    boolean canRemoveLineFromWave   = whsKanbanWaveTableManagePLPoolForm.canRemoveLineFromWave(whsWaveTable, whsKanbanWaveLine);

    addKanbanJobPickingList.enabled(canAddLineToWave);
    addAllShipment.enabled(canAddLineToWave);
    remove.enabled(canRemoveLineFromWave);
    removeAll.enabled(canRemoveLineFromWave);
}

public void init()
{
    if (element.args().dataset() == tableNum(WHSWaveTable))
    {
        waveId = element.args().record().(fieldNum(WHSWaveTable, WaveId));
    }

    super();

    whsKanbanWaveTableManagePLPoolForm = WHSKanbanWaveTableManagePLPoolForm::newStandard(whsWaveTable_ds,whsKanbanWaveLine_ds,whsKanbanJobPickingListPool_ds);

    this.setPoolFilter();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    element.enableAddRemoveButtons();

    imageList = new WHSImageListAppl_WHS();
    imageList.build();
}

/// <summary>
/// Returns the dimension displays object.
/// </summary>
/// <returns>
/// The dimensions display object
/// </returns>
public InventDimCtrl_Frm_WHS inventDimSetupObject()
{
    return inventDimFormSetup;
}

public void setPoolFilter()
{
    QueryBuildRange qbr;

    qbr = whsKanbanJobPickingListPool_q.dataSourceTable(tableNum(KanbanJobPickingListAll)).addRange(fieldNum(KanbanJobPickingListAll,ItemId));

    // Add range so that only pool records that aren't fully picked show up
    qbr.value(strFmt('(%1.%2 > %3.%4)',
                     tableStr(KanbanJobPickingListAll),
                     fieldStr(KanbanJobPickingListAll,Quantity),
                     tableStr(WHSKanbanJobPickingListPool),
                     fieldStr(WHSKanbanJobPickingListPool,WavedQty)));

    whsKanbanJobPickingListPool_ds.executeQuery();
}

/// <summary>
/// Updates the dimensions display object.
/// </summary>
/// <param name="_mode">
/// The state of the dimensions display form.
/// </param>
/// <exception cref="Exception::Error">
/// Incorrect parameter for the function.
/// </exception>
public void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm inventDimParmLocal;

    InventDimGroupSetup::newItemId(whsKanbanJobPickingListPool.ItemId);
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_WHS::newFromForm(element);
            }

            inventDimParmLocal.setActiveSiteAndWarehouseDimensions();
            inventDimParmLocal.wmsLocationIdFlag = NoYes::Yes;
            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParmLocal);
        // do continue
        case InventDimFormDesignUpdate::Active:
            inventDimFormSetup.formActiveSetup();
            inventDimFormSetup.formSetControls(true);
            break;

        default:
            throw error(strFmt("@SYS54195",funcName()));
    }
}

public void init()
{
    super();

    curWaveRange = whsWaveTable_ds.query().dataSourceName(whsWaveTable_ds.name()).addRange(fieldNum(WHSWaveTable, WaveId));

    if (waveId)
    {
        curWaveRange.value(waveId);
    }
    else
    {
        curWaveRange.value(SysQuery::valueEmptyString());
    }
}

public void init()
{
    QueryBuildDataSource    qbds;

    super();

    qbds = whsWaveTableFilter_ds.query().dataSourceName(whsWaveTableFilter_ds.name());
    qbds.clearRanges();
    qbds.clearDynalinks();

    qbds.addRange(fieldNum(WHSWaveTable, WaveStatus)).value(enum2str(WHSWaveStatus::Created));
}

public int active()
{
    int ret;

    ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active);

    element.enableAddRemoveButtons();

    return ret;
}

public int active()
{
    int ret;

    ret = super();

    element.enableAddRemoveButtons();

    return ret;
}

public void clicked()
{
    super();
    whsKanbanWaveTableManagePLPoolForm.deleteSelectedWaveLines();

    whsWaveTable_ds.executeQuery();
    whsKanbanJobPickingListPool_ds.executeQuery();
    whsKanbanWaveLine_ds.executeQuery();
}
