using System.Text;

[Form]
public class WHSLocDirTable extends FormRun implements WHSIQueryPartCallback
{
    private const str WHSLocDirQueryPart_Range = menuItemDisplayStr(WHSLocDirQueryPart_Range);
    private const str WHSLocDirActionQueryPart_Range = menuItemDisplayStr(WHSLocDirActionQueryPart_Range);
    private const str WHSLocDirActionQueryPart_Sorting = menuItemDisplayStr(WHSLocDirActionQueryPart_Sorting);
    private WHSIQueryPart headerRangePart;
    private WHSIQueryPart lineRangePart;
    private WHSIQueryPart lineSortingPart;

    protected QueryBuildRange     workTransTypeRange;
    protected QueryRun            queryRun;
    protected WHSLocDirTableForm  whsLocDirTableForm;

    [SysObsolete('This class member is deprecated because it is not used in this class anymore.', false, 25\10\2023)]
    protected boolean isWHSLocDirTableEnabledDispCodeMethodFlightEnabled = true;

    private WHSLocationDirectiveAcceptanceTestResultContract acceptanceTestContract;
    private readonly int highlightSucceedingColor = WinAPI::RGB2int(0xA0, 0xFF, 0xA0);
    private readonly int highlightVisitingColor = WinAPI::RGB2int(0xFF, 0xFF, 0xA0);

    private QueryBuildRange inventLocationIdRange;
    private QueryBuildRange warehouseGroupLinkRange;

    private QueryBuildDataSource qbdsLocationDirective;
    private QueryBuildDataSource qbdsWarehouseGroup;
    private QueryBuildDataSource qbdsGroupLink;

    private WHSSequenceNumberMoverForm locationDirectivesSequenceNumberMover;

}

public void init()
{
    whsLocDirTableForm = new WHSLocDirTableForm();

    super();

    WHSWorkTransType transType = element.args().parmEnum();

    workTransType.selection(enum2int(transType));

    this.initButtons();
    this.updateControls();     

    WHSSequenceNumberMoverForm::newFromDataSourceAndQuery(
            WHSLocDirTable_ds,
            fieldStr(WHSLocDirTable, LocSeqNum))
        .initNewButtons(ButtonGroup);

    WHSSequenceNumberMoverForm::newFromDataSource(
            WHSLocDirLine_ds,
            fieldStr(WHSLocDirLine, LocSeqNum),
            [fieldStr(WHSLocDirLine, RefRecId)])
        .initNewButtons(ButtonGroupLine);

    WHSSequenceNumberMoverForm::newFromDataSource(
            WHSLocDirAction_ds,
            fieldStr(WHSLocDirAction, LocSeqNum),
            [fieldStr(WHSLocDirAction, RefRecId)])
        .initNewButtons(ButtonGroupActions);

    ButtonMoveUpLocation.visible(false);
    ButtonMoveDownLocation.visible(false);
    ButtonMoveUpLine.visible(false);
    ButtonMoveDownLine.visible(false);
    ButtonMoveUpActions.visible(false);
    ButtonMoveDownActions.visible(false);


    this.initQueryFormParts();

}

private void resetHighlightForDataSource(FormDataSource _formDataSource)
{
    for (Common bufferLocal = _formDataSource.getFirst(); bufferLocal; bufferLocal = _formDataSource.getNext())
    {
        _formDataSource.clearDisplayOption( bufferLocal );
    }
}

private boolean isContractChanged(WHSLocationDirectiveAcceptanceTestResultContract _contract)
{
    int contractsProvided = 0;
    contractsProvided += _contract ? 1 : 0;
    contractsProvided += acceptanceTestContract ? 1 : 0;

    switch (contractsProvided)
    {
        case 1:
            // Only one of them is set, so something changed
            return true;
        case 2:
            return acceptanceTestContract.pack() != _contract.pack();
    }

    return false;
}

public void applyAcceptanceTestContract(WHSLocationDirectiveAcceptanceTestResultContract _contract)
{
    if (this.isContractChanged(_contract))
    {
        acceptanceTestContract = _contract;

        this.resetHighlightForDataSource(WHSLocDirAction_ds);
        this.resetHighlightForDataSource(WHSLocDirLine_ds);
        this.resetHighlightForDataSource(WHSLocDirTable_ds);

        WHSLocDirTable_ds.research(true);
    }
}

public void applyHighlight(Common _record, FormRowDisplayOption _options)
{
    if (acceptanceTestContract)
    {
        if (acceptanceTestContract.isRecordSucceeding(_record))
        {
            _options.backColor(highlightSucceedingColor);
            _options.colorOnSelectedRow(true);
        }
        else if (acceptanceTestContract.isRecordVisited(_record))
        {
            _options.backColor(highlightVisitingColor);
            _options.colorOnSelectedRow(true);
        }
    }
}

private void initQueryFormParts()
{
    str fieldNameHeader = fieldId2Name(tableNum(whsLocDirTable), fieldNum(whsLocDirTable, LocDirQuery));
    str fieldNameRecIdHeader = fieldId2Name(tableNum(whsLocDirTable), fieldNum(whsLocDirTable, RecId));
    headerRangePart = WHSQueryHelper::initializeQueryFormPart(element, WHSLocDirQueryPart_Range, fieldNameHeader, '%1', fieldNameRecIdHeader, whsLocDirTable.defaultLocationDirectiveQueryRun(), true, false);
    element.setQueryPartDataVisibility();

    str fieldNameLine = fieldId2Name(tableNum(whsLocDirAction), fieldNum(whsLocDirAction, LocDirQuery));
    str fieldNameRecIdLine = fieldId2Name(tableNum(whsLocDirAction), fieldNum(whsLocDirAction, RecId));
    str fieldNameRefRecIdLine = fieldId2Name(tableNum(whsLocDirAction), fieldNum(whsLocDirAction, RefRecId));
    lineRangePart = WHSQueryHelper::initializeQueryFormPart(element, WHSLocDirActionQueryPart_Range, fieldNameLine, "@WAX:LocationDirectiveLineActionQueryName", fieldNameRefRecIdLine, whsLocDirAction.defaultLocationQueryRun(), true, false, fieldNameRecIdLine);
    lineSortingPart = WHSQueryHelper::initializeQueryFormPart(element, WHSLocDirActionQueryPart_Sorting, fieldNameLine, "@WAX:LocationDirectiveLineActionQueryName", fieldNameRefRecIdLine, whsLocDirAction.defaultLocationQueryRun(), false, true, fieldNameRecIdLine);
}

private void setQueryPartDataVisibility()
{
    if(headerRangePart)
    {
        if(buttonEditQueryLocation.enabled())
        {
            headerRangePart.showPartData();
        }
        else
        {
            headerRangePart.hidePartData();
        }
    }
}

public void queryUpdated(FormRun _sender, container _packedQuery)
{
    str menuItemName = _sender.args().menuItemName();

    if(menuItemName == WHSLocDirQueryPart_Range)
    {
        whsLocDirTable.LocDirQuery = _packedQuery;
        whsLocDirTable.write();
    }
    else if(menuItemName == WHSLocDirActionQueryPart_Range)
    {
        whsLocDirTableForm.writeWHSLocDirAction(whsLocDirAction, _packedQuery);

        lineSortingPart.refresh();
    }
    else if(menuItemName == WHSLocDirActionQueryPart_Sorting)
    {
        whsLocDirTableForm.writeWHSLocDirAction(whsLocDirAction, _packedQuery);

        lineRangePart.refresh();
    }
}

